INSERT INTO `yazilar` (`yazi_id`, `yazi_foto`, `yazi_title`, `yazi_icerik`, `yazi_tarih`, `yazi_kategori_id`, `yazi_kategori_alt_id`, `yazi_okunma`, `yazi_seolink`, `yazi_keyw`, `yazi_desc`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, '132944394_personel.JPG', 'Twitter Hesabı Nasıl Dondurulur?', '<p style=\"text-align:justify\"><span style=\"font-family:Calibri,sans-serif; font-size:20px\">Milyonlarca kişilik kullanıcı kitlesine sahip olan Twitter, d&uuml;nyanın &ouml;nde gelen sosyal medya platformları arasında. Bu platformla ilgili en &ccedil;ok merak edilen konulardan birisi de hesap dondurma işleminin nasıl yapıldığı.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter Hesabı Dondurma</strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">G&uuml;n&uuml;m&uuml;z&uuml;n en pop&uuml;ler sosyal medya platformlarından birisi olan Twitter&rsquo;da, en b&uuml;y&uuml;k merak konularından bir tanesi de <strong>Twitter hesabını dondurmak</strong>tır. Dondurma işlemi genelde hesabını ge&ccedil;ici bir s&uuml;reliğine kapatmak isteyen kullanıcıların başvurduğu bir y&ouml;ntem. Bu şekilde hesabınızı kapatabilir, daha sonra a&ccedil;mak istediğinizde yine kullanıcı adı ve şifrenizle giriş yaparak her şeyi bıraktığınız gibi bulabilirsiniz. Hesabınızı dondurduğunuzda i&ccedil;indeki t&uuml;m bilgilerin saklanacağını ancak hesabınızı a&ccedil;madığınız s&uuml;rece bu bilgilere siz dahil kimsenin erişemeyeceğini bilmenizde fayda var. Bu nedenle dondurma işlemini yapmadan &ouml;nce eğer hesabınızda ihtiya&ccedil; duyabileceğiniz fotoğraflar, bilgiler vs. varsa işlemi yapmadan &ouml;nce bunları kaydetmenizi &ouml;neririz. </span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Hesap Dondurma İşlemi</strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter hesabınızı dondurmak</strong> i&ccedil;in olduk&ccedil;a kolay belli başlı adımları takip etmeniz gerekmekte. İlk olarak Twitter hesabınıza kullanıcı adı ve şifrenizle giriş yapın. Giriş yaptıktan sonra ana sayfanın sol yanında bulunan &ldquo;Daha fazla&rdquo; butonuna tıklayın. Butona tıkladıktan sonra karşınıza &ccedil;ıkacak men&uuml;den &ldquo;Ayarlar ve Gizlilik&rdquo; butonuna tıklayın. Bu işlemin ardından a&ccedil;ılacak ayarlar men&uuml;s&uuml;nde &ldquo;Hesap&rdquo; butonuna tıklayın ve sayfayı aşağı kaydırın. Sayfanın en altında g&ouml;receğiniz &ldquo;Devre dışı bırak&rdquo; butonuna tıklayın. &ldquo;Devre dışı bırak&rdquo; butonuna basmanızla birlikte hesap dondurma işleminizi başarıyla tamamlamış olacaksınız.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter Hesabını Dondurmadan &Ouml;nce Dikkat Edilmesi Gerekenler</strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Hesap dondurma işlemine başlamadan &ouml;nce dikkat etmeniz bazı konular var. <strong>Twitter hesabını dondur</strong>duğunuzda insanlar size Twitter &uuml;zerinden ulaşım sağlayamaz. Bu y&uuml;zden hesabınızı dondurmadan &ouml;nce bunun bilgisini iletişim kurduğunuz kişilere vermenizde fayda var. Bunun yanında daha &ouml;nce de s&ouml;ylemiş olduğumuz gibi fotoğraf yedekleme var. Eğer Twitter hesabınızı dondurmuş ve yakın zamanda tekrar a&ccedil;mak istemiyorsanız hesabınızda ihtiyacınız olabileceğini d&uuml;ş&uuml;nd&uuml;ğ&uuml;n&uuml;z fotoğraf ve videoları yedeklemenizi &ouml;neririz. Bunun sebebi Twiiter hesabınızı dondurduğunuz zaman paylaşmış olduğunuz fotoğraf ve videolara erişemiyor oluşunuz. Yedekleme yapmadığınız takdirde gerekli fotoğraflara erişebilmek i&ccedil;in dondurduğunuz hesabı tekrar a&ccedil;manız gerekebilir.</span></span></p>\r\n', '2020-12-21 18:58:59', 0, 0, '0', 'twitter-hesabi-nasil-dondurulur', 'twitter dondurma', 'Twitter hesabınızı dondurmak için oldukça kolay belli başlı adımları takip etmeniz gerekmekte. İlk olarak Twitter hesabınıza kullanıcı adı ve şifrenizle giriş yapın. Giriş yaptıktan sonra ana sayfanın sol yanında bulunan...', '2021-11-23 17:08:59', '2021-11-25 12:23:20', NULL),
(2, '132944394_personel.JPG', 'Twitter Hesabı Nasıl Silinir?', '<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter, g&uuml;n&uuml;m&uuml;zde milyonlarca kişinin kullanmakta olduğu olduk&ccedil;a pop&uuml;ler bir sosyal medya platformu. Her g&uuml;n y&uuml;zlerce yeni hesap a&ccedil;ılırken aynı zamanda bir o kadar da hesap silinmekte.</span></span></p>\r\n\r\n<h2 style=\"text-align:justify\">&nbsp;</h2>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Bilgisayarda Twitter Hesabı Silme</strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Eğer sahip olduğunuz <strong>Twitter hesabını silmek</strong> istiyorsanız bunu kolaylıkla yapabilirsiniz. Bu işlemi bir bilgisayarda yapacaksanız izlemeniz gereken bazı adımlar var. İlk olarak internet tarayıcınızdan Twitter internet sitesine girin. Ardından profil simgenizin yanındaki a&ccedil;ılır men&uuml;den &ldquo;Ayarlar ve Gizlilik&rdquo; se&ccedil;eneğine tıklayın. A&ccedil;ılacak sayfada &ouml;nce &ldquo;Hesap&rdquo; sekmesine ardından da sayfanın en altında bulunan &ldquo;Hesabımı devre dışı bırak&rdquo; se&ccedil;eneğine tıklayın. Hesabınızı devre dışı bırakmaya ilişkin bilgileri okuduktan sonra &ldquo;Kullanıcı adını devre dışı bırak&rdquo; se&ccedil;eneğine dokunun. Sistem sizden kullanıcı adı ve şifrenizi istediğinde gerekli bilgilerinizi girin ve &ldquo;Hesabı devre dışı bırak&rdquo; butonuna tıklayarak işleminizi tamamlayın.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Telefonda Twitter Hesabı Silme</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Eğer kullandığınız <strong>Twitter hesabını silme</strong> işlemini telefonunuz &uuml;zerinden ger&ccedil;ekleştirmek istiyorsanız işiniz yine olduk&ccedil;a kolay. Bunun i&ccedil;in ilk olarak telefonunuza y&uuml;klediğiniz Google Chrome internet tarayıcısını a&ccedil;ıp Twitter internet sitesine girmelisiniz. Kullanıcı adı ve şifrenizle giriş yaptıktan sonra ekranın sağ &uuml;st k&ouml;şesinde bulunan 3 nokta işaretine tıklayıp &ldquo;Masa&uuml;st&uuml; Sitesi İste&rdquo; se&ccedil;eneğine dokunun. Bu sayede Twitter&rsquo;ın bilgisayar g&ouml;r&uuml;n&uuml;m&uuml;ne erişim sağlamış olacaksınız. Profilinize tıklayıp ayarlar men&uuml;s&uuml;n&uuml; a&ccedil;ın. Ayarlar men&uuml;s&uuml;n&uuml;n en altında yer alan &ldquo;hesabımı devre dışı bırak&rdquo; se&ccedil;eneğine dokunun. Devre dışı bırakma işlemine ilişkin bilgileri okuyun ve ardından &ldquo;Devre dışı bırak&rdquo; se&ccedil;eneğine dokunun. Sistem sizden kullanıcı adınızı ve şifrenizi istediğinde gerekli bilgileri girdikten sonra &ldquo;Devre dışı bırak&rdquo; ve ardından tekrar &ldquo;Evet, devre dışı bırak&rdquo; se&ccedil;eneklerine dokunarak işleminizi tamamlayın.</span></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Hesap Silme Hakkında Bilmeniz Gerekenler</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kullanıcı adınızı veya e-postanızı başka bir hesapta kullanmaya devam etmek istiyorsanız, bu hesabı devre dışı bırakmadan &ouml;nce kullanıcı adı ve e postanızı değiştirip onaylamalısınız.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Hesabınıza kayıtlı kullanıcı adını veya e postayı değiştirmek i&ccedil;in hesabınızı devre dışı bırakmanız gerekmemektedir. Ayarlar men&uuml;s&uuml;nden bu işlemleri kolaylıkla yapabilirsiniz.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:48px; text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Yanlışlıkla silme gibi durumlara karşın, <strong>Twitter hesabını devre dışı bırakma</strong> işlemini yapmanızın ardından gelecek 30 g&uuml;n boyunca Twitter hesabınızı yeniden etkinleştirme şansınız vardır.</span></span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:24px\">&nbsp;</p>\r\n', '2020-12-22 14:27:10', 0, 0, '0', 'twitter-hesabi-nasil-silinir', 'twitter hesap silme', 'Eğer sahip olduğunuz Twitter hesabını silmek istiyorsanız bunu kolaylıkla yapabilirsiniz.Twitter internet sitesine girin. Ardından profil simgenizin yanındaki açılır menüden “Ayarlar ve Gizlilik” seçeneğine tıklayın. Açılacak sayfada...', '2021-11-23 17:08:59', '2021-11-25 12:23:29', NULL),
(3, '702002695_claudio-schwarz-purzlbaum-FMJAYeRwtDY-unsplash.jpg', 'Twitter Hesabı Nasıl Kapatılır?', '<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">G&uuml;n&uuml;m&uuml;z&uuml;n en &ccedil;ok kullanıcıya hitap eden sosyal medya platformlarından biri olan Twitter hakkında en &ccedil;ok sorulan sorulardan birisi de Twitter hesabının nasıl kapatıldığı.</span></span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter Hesabını Kalıcı Olarak Kapatma</strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter hesabını kapatmak</strong> i&ccedil;in iki se&ccedil;eneğiniz var. Bunlardan birisi hesabı kalıcı olarak kapatmak diğeri ise ge&ccedil;ici bir s&uuml;reliğine hesabınızı dondurmak.&nbsp; Eğer hesabınızı kalıcı olarak kapatmak istiyorsanız yapmanız gereken ilk şey internet tarayıcınızdan twitter&rsquo;ın internet sitesine girip kullanıcı adınız ve parolanızla giriş yapmak. Ardından profil simgenizin yanında bulunan a&ccedil;ılır men&uuml;den &ldquo;ayarlar ve gizlilik&rdquo; butonuna tıklamalısınız. Karşınıza &ccedil;ıkacak sayfada &ouml;nce &ldquo;hesap&rdquo; butonuna, ardından da a&ccedil;ılan sayfanın en altında bulunan &ldquo;hesabımı devre dışı bırak&rdquo; butonlarına sırasıyla tıklayın. Devre dışı bırakma işlemiyle ilgili bilgileri okuduktan sonra &ldquo;kullanıcı adını serbest bırak&rdquo; butonuna tıklayın. Sistem sizden kullanıcı adınızı ve parolanızı istediğinde gerekli bilgileri girin ve son olarak &ldquo;hesabı devre dışı bırak&rdquo; butonuna tıklayarak <strong>Twitter hesabı silme</strong> işleminizi sonlandırın. B&ouml;ylece hesabınızı tamamen silmiş olacaksınız. Bu y&uuml;zden hesapta bulunan işe yarar fotoğraf, video gibi &ouml;gelerinizi yedeklemeyi ihmal etmeyin. Bunun yanında eğer kalıcı olarak kapattığınız hesabın kullanıcı adını veya e postasını farklı bir Twitter hesabınızda kullanmayı d&uuml;ş&uuml;n&uuml;yorsanız hesabı kapatmadan &ouml;nce farklı hesapta kullanmak istediğiniz kullanıcı adı veya e posta adresini değiştirip onaylamalısınız. </span></span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter Hesabını Ge&ccedil;ici Olarak Kapatma</strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter hesabınızı silmek istemiyor ancak kısa bir ara vermenin iyi geleceğini d&uuml;ş&uuml;n&uuml;yorsanız <strong>Twitter hesabınızı ge&ccedil;ici olarak kapatma</strong> yoluna başvurabilirsiniz. Hesabınızı kısa s&uuml;re kapatmak yani dondurmak i&ccedil;in ilk olarak internet tarayıcınızdan Twitter&rsquo;ın internet sitesine girip Twitter hesabınıza giriş yapmalısınız. Hesabınıza giriş yaptığınızda a&ccedil;ılacak ana sayfanın sol tarafında bulunan &ldquo;Daha Fazla&rdquo; butonuna tıklayın. Bu butona tıkladığınızda karşınıza &ccedil;ıkacak men&uuml;de &ouml;nce &ldquo;Ayarlar ve Gizlilik&rdquo; butonuna ardından da a&ccedil;ılacak ayarlar men&uuml;s&uuml;ndeki &ldquo;Hesap&rdquo; butonuna tıklayın. A&ccedil;ılan sayfanın en altına indiğinizde &ldquo;Devre dışı bırak&rdquo; butonunu g&ouml;receksiniz. Bu butona tıklayın. Bu şekilde hesabınızı dondurmuş olacaksınız.Hesabınızı dondurduğunuzda siz dahil kimsenin hesabınıza erişim sağlayamayacağından dolayı eğer gerekli olabilecek fotoğraf, videolarınız varsa bunları yedeklemenizde fayda var.</span></span></p>\r\n', '2020-12-23 13:51:44', 0, 0, '0', 'twitter-hesabi-nasil-kapatilir', 'twitter hesap kapatma', 'Twitter hesabını kapatmak için iki seçeneğiniz var. Bunlardan birisi hesabı kalıcı olarak kapatmak diğeri ise geçici bir süreliğine hesabınızı dondurmak.  Eğer hesabınızı kalıcı olarak kapatmak istiyorsanız yapmanız gereken', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(4, '1790129545_twitter-292994_1280.jpg', 'Twitter Kullanıcı Adınızı Ve Görünen Adımı Nasıl Değiştirebilirim?', '<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:#232323\">Twitter kullanıcı adınız zamanla değiştirmek isteyebilirsiniz</span></strong><strong><span style=\"color:#232323\">.</span></strong><strong><span style=\"color:#232323\"> İşte bu g&uuml;n ki makelemizde size twitter kulanıcı adınızı nasıl değiştirebileceğinizi detaylı olarak anlattık.</span></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kullanıcı adlar twitter &uuml;zerinde farklılık g&ouml;sterebilir, bu sebeple kullanıcı adınızı insanların sizi g&ouml;rmesini istediğiniz gibi ayarlayabilirsiniz. Neyse ki, hem kullanıcı adınızı hem de Adı alanını Twitter&rsquo;da değiştirmek isterseniz bu işlem gayet kolaydır ve istediğiniz kadar değiştirme yapabilirsiniz.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Değiştirilebilir şekilde kullanılmasına rağmen,&nbsp;Twitter&nbsp;kullanıcı adınız ve profildeki adı alanı aynı alanlar değildir. Kullanıcı adınızın, benzersiz&nbsp;olması gerekmektedir ve profil URL&#39;nizde g&ouml;r&uuml;nen ve giriş yapmanızı sağlayan alandır. Tweet&#39;leri retweet ve yanıtlama durumlarında başkalarına direckt mesaj g&ouml;ndermenizi sağlayan alandır. (@ işaretinden sonra gelen kısımdır). Diğer yandan, g&ouml;r&uuml;nen adınız benzersiz olmasına gerek yoktur birden &ccedil;ok kişi de aynı adı kullanabilir ve profilinizde kullanıcı adınızın hemen &uuml;st&uuml;nde g&ouml;r&uuml;nen alandır.</span></span></p>\r\n\r\n<h2><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"background-color:white\"><span style=\"color:#232323\">Twitter kullanıcı adımı nasıl değiştiririm?</span></span></strong></span></span></h2>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:#232323\">Twitter hesabınıza giriş yapın</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kullanıcı adınız veya e-mail şifreniz ile twitter hesabınıza giriş yapınız. Not: Bu işlemleri yapmak i&ccedil;in telefon veya tablet kullanabilir twitter&rsquo;in mobil uygulamasından da giriş yapabilirsiniz.</span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:#232323\">Hesap Ayarlarına Erişim</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Ardından, web sitesi i&ccedil;in sol tarafta en alttaki b&ouml;l&uuml;mden Daha Fazla&gt; Ayarlar ve gizlilik se&ccedil;eneğine tıklayın veya uygulamadaki profil resminize dokunduktan sonra en altta Ayarlar ve gizlilik men&uuml;s&uuml;n&uuml; se&ccedil;iniz. Hesaba Git. Hesap sekmesi web sitesinde varsayılan olarak a&ccedil;ılmalıdır, ancak twitter&rsquo;ın uygulamadaki Hesap se&ccedil;eneğini se&ccedil;erek devam edebilirsiniz.</span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:#232323\">Adım: Kullanıcı adınızı değiştirin</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kullanıcı adı kısmını g&ouml;receksiniz. Kullanıcı adını se&ccedil;tikten sonra kullanıcı adını metin alanına istediğiniz kullanıcı adını yazın. Maksimum 15 karakter olacak şekilde istediğiniz kullanıcı adınızı giriniz. İlk yazdığınız kullanıcı adı uygun g&ouml;r&uuml;lmediyse veya daha &ouml;nce kullanılmış bir kullanıcı adı yazdıysanız twitter size başka bir kullanıcı adı se&ccedil;meniz gerektiğini uyarı ile g&ouml;sterecektir.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Yeni oluşturacağınız kullanıcı adınız &ouml;zel karakter i&ccedil;eremez, alfan&uuml;merik karakterler i&ccedil;erebilir(A-z,0-9) ve rozetli bir&nbsp;twitter hesabınız yok ise hem &ldquo;twitter&rdquo; kelimesi hem de &ldquo;admin&rdquo; kelimelerini kullanıcı adınızda kullanmanıza twitter izin vermemektedir. </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Not: Rozetli bir hesabınız varsa (kullanıcı adınızın yanındaki mavi onay işareti), kullanıcı adınızı değiştirirseniz rozetinizi kaybedersiniz. </span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter&#39;da g&ouml;r&uuml;nen adınızı nasıl değiştirirsiniz?</strong></span></span></h2>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:#232323\">Twitter Hesabınıza giriş yapın</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kullanıcı adınız ve şifrenizi girerek Twitter&#39;da hesabınızı a&ccedil;ınız</span></span></p>\r\n\r\n<ol start=\"2\">\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:#232323\">Profilinize gidin</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Profile gidebilmeniz i&ccedil;in masa&uuml;st&uuml;nde sol men&uuml;de profil yazısına tıklayınız. Mobil ve uygulama i&ccedil;in ise sol men&uuml;den profil fotoğrafınıza veya profil yazısına tıklayınız</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Ardından a&ccedil;ılan profilinizde, Ekranın sağ &uuml;st tarafındaki profili d&uuml;zenle yazısına tıklayın. </span></span></p>\r\n\r\n<ol start=\"3\">\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"color:#232323\">Twitter adınızı değiştirin</span></strong></span></span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Burdan sonra profil resminizin hemen altında bulunan İsim metin alanına g&ouml;r&uuml;nmesini istediğiniz adınızı girebilirsiniz. Şu anda maksimum 50 karakter olacak şekilde twitter adı girebiliyorsunuz aynı zamanda 50 karakterden daha az olabilir. Yazımızın başında belirttiğimiz gibi kullanıcı adınızdan farklı olarak, g&ouml;r&uuml;nen adınızın hesabınıza &ouml;zg&uuml; olması gerekmez birden &ccedil;ok okan olabilir değil mi ?Ve hatta adınıza emojiler bile olabilir.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Adınız belirledikten sonra, yeni adınızı kaydetmek i&ccedil;in profili d&uuml;zenle sayfasının sağ tarafındaki Kaydet butonuna tıklayın.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Adınızı ve kullanıcı adınızı Twitter &uuml;zerinden bu adımlardan yardım alarak değiştirebilirsiniz. Mutlu ve sağlıklı g&uuml;nler..</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '2020-12-24 17:05:29', 0, 0, '0', 'twitter-kullanici-adinizi-ve-gorunen-adimi-nasil-degistirebilirim', 'Twitter kullanıcı adınızı ve görünen adımı nasıl değiştirebilirim', 'Neyse ki, hem kullanıcı adınızı hem de Adı alanını Twitter’da değiştirmek isterseniz bu işlem gayet kolaydır ve istediğiniz kadar değiştirme yapabilirsiniz.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(5, '921873843_pexels-tracy-le-blanc-607812 (1).jpg', 'Twitter en çok takipçisi olan kişiler', '<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Global olarak en pop&uuml;ler sosyal medyalardan birisi olan Twitter, her ge&ccedil;en g&uuml;n dahada b&uuml;y&uuml;meye devam ediyor. Peki sizce<strong> </strong><strong>twitter&rsquo;ın en &ccedil;ok takip&ccedil;iye sahip hesap kimin?</strong><strong> </strong>İlk s&ouml;yleyişte insanda bir merak uyandırıyor sizce de &ouml;yle değil mi gelin hep beraber bunun cevabını bulalım.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Son zamanların en pop&uuml;ler platformlarından birisi olan <strong>twitter &uuml;zerinde aylık 71 Mn aktif kullanıcının ziyaret ettiği</strong> ve her ge&ccedil;en g&uuml;n daha fazla kişinin de bu mecraya kayıt olduğu biliniyor.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kullanıcılarına her paylaşım i&ccedil;in alıntılama,yanıtlama, retweet ve beğeni gibi farklı etkileşim se&ccedil;enekleri sunan platformda sporcudan sanat&ccedil;ılara kadar bir&ccedil;ok &uuml;nl&uuml;, takip&ccedil;ileriyle bir araya geliyor. Twitter&rsquo;da en &ccedil;ok takip&ccedil;isi olan hesaplar doğal olarak en &ccedil;ok beğeni, reetwet gibi etkileşimin ger&ccedil;ekleştiği hesaplar olarak dikkat &ccedil;ekiyor. Buraya kadar okuduktan sonra &quot;<strong>Twitter&#39;da en &ccedil;ok takip&ccedil;isi olan hesaplar</strong>&quot; hangileri acaba diye merak oluştuysa doğru yerdesiniz vakit kaybetmeden ge&ccedil;elim mi sorumuzun yanıtına.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Barack Obama&nbsp;</span></span><span style=\"background-color:white\"><span style=\"color:#333333\">127.4 Mn takip&ccedil;i</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Justin Bieber </span></span><span style=\"background-color:white\"><span style=\"color:#333333\">113.4 Mn takip&ccedil;i</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:20px\"><span style=\"color:#333333\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\">Katty Perry </span><span style=\"background-color:white\">109 Mn&nbsp; takip&ccedil;i</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Rihanna&nbsp;</span></span><span style=\"background-color:white\"><span style=\"color:#333333\">100.1 Mn takip&ccedil;i</span></span></span></span></p>\r\n	</li>\r\n	<li>\r\n	<p><span style=\"font-size:20px\"><span style=\"color:#333333\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\">Taylor Swift&nbsp;&nbsp;</span><span style=\"background-color:white\">87.8 Mn takip&ccedil;i</span></span></span></span></p>\r\n	</li>\r\n</ol>\r\n\r\n<p>&nbsp;</p>\r\n', '2020-12-24 18:56:01', 0, 0, '0', 'twitter-en-cok-takipcisi-olan-kisiler', 'Twitter en çok takipçisi olan kişiler', ' Peki sizce twitter’ın en çok takipçiye sahip hesap kimin? İlk söyleyişte insanda bir merak uyandırıyor sizce de öyle değil mi gelin hep beraber bunun cevabını bulalım.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(6, '738471664_pexels-pixabay-267350.jpg', 'Twitterda En Çok Takipçiye Sahip Olan Türk Hesaplar', '<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">D&uuml;nya &uuml;zerinde olduğu gibi T&uuml;rkiye&rsquo;de en &ccedil;ok kullanılan sosyal medyalardan biri olan twitterda t&uuml;rk &uuml;nl&uuml;ler&#39;de bir hayli takip&ccedil;i kitlesine sahip. T&uuml;m d&uuml;nyada 390 Mn kayıtlı kullanıcısı olan bu platform, T&uuml;kiyede&nbsp;de olduk&ccedil;a kullanılıyor.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">&Uuml;lkemizde yaşanan &ccedil;oğu olay i&ccedil;in g&ouml;sterilen tepkiler bu platformdan yapıldığı zaman ciddi derecede etkili oluyor ve genellikle twitter kullanıcılarının amacı g&uuml;ndem toplumsal sorunlar olarak belirlenmiştir. </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Bu y&uuml;zden sadece eğlence paylaşımları değil toplumsal olayların da tartışıldığı bir sosyal medya durumunda.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">T&uuml;rkiyede de Twitter&rsquo;da T&uuml;rk &uuml;nl&uuml;ler arasında ilk sırada 14.7Mn&nbsp;takip&ccedil;isi olan Cem Yılmaz var. Cem Yılmazdan sonra ise 7Mn&nbsp;takip&ccedil;isiyle C&uuml;neyt &Ouml;zdemir geliyor. Bu ilk 2 nin ardında n gelen &uuml;nl&uuml;lerle oluşturuğumuz sıralamaya beraber g&ouml;z atalım.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Cem Yılmaz 14.7 Mn takip&ccedil;i</span></span></span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">C&uuml;neyt &Ouml;zdemir 7 Mn takip&ccedil;i</span></span></span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Demet Akalın 6.7 Mn takip&ccedil;i</span></span></span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Ata Demirer 6.7 Mn takip&ccedil;i</span></span></span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:white\"><span style=\"color:#333333\">Sıla Gen&ccedil;oğlu 5.6 Mn takip&ccedil;i</span></span></span></span></li>\r\n</ol>\r\n', '2020-12-24 19:10:33', 0, 0, '0', 'twitterda-en-cok-takipciye-sahip-olan-turk-hesaplar', 'Twitterda En Çok Takipçiye Sahip Olan Türk Hesaplar', 'Türkiye’de Twitter’da Türk ünlüler arasında ilk sırada 14.7 Mn takipçisi olan Cem Yılmaz var. Cem Yılmazdan sonra ise 7 Mn takipçisiyle Cüneyt Özdemir geliyor.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(7, '2063205576_pexels-solen-feyissa-5744251.jpg', 'Twitter Hesap Alımı Sırasında Dikkat Edilmesi Gerekenler', '<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Sosyal medyanın en pop&uuml;ler uygulamalarından birisi olarak tercih edilen <strong>Twitter</strong>, her ge&ccedil;en g&uuml;n kullanıcı sayısında y&uuml;kselmeye devam ederek en pop&uuml;ler &nbsp;sosyal medyalar arasında ilk 4 e girmiş bulunmaktır. Bu mecra i&ccedil;erisinde var olmak isteyenlerin sıklıkla yaşadığı problemler takip&ccedil;i, beğeni, retweet gibi etkileşim gerektiren unsurları y&uuml;kseltme istekleri profesyonel kişi ya da platformlar i&ccedil;erisinde &ccedil;&ouml;z&uuml;me kavuşturmak istemektedir. &Ouml;zellikle <strong>ticari ama&ccedil;la a&ccedil;ılan</strong> <strong>twitter hesapları</strong>, belirli bir kitleye sahip olana&nbsp;kadar aktif bir izlenim yaratmak adına takip&ccedil;i(bot) alımları yapmaktadır.</span></span></p>\r\n\r\n<h2><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter Hesabı&nbsp;Alınırken&nbsp;Dikkat Edilecek Unsurlar</strong></span></span></h2>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:#f5f5ff\">Alacağınız twitter ilk mail olduğundan emin olmanız gerekir.Aynı zamanda <strong>alacağınız twitter hesabının</strong> ilk sahibi daha &ouml;nceden başkasına satmış ve satın alan kişi değiştirip hesabı size satabilir. O sebepten &ouml;t&uuml;r&uuml; alacağınız <strong>twitter hesabının ilk e-maili</strong> olup olmadığını sorun daha &ouml;ncesinde sorun alıcağınız hesabın g&uuml;venilir kişiden veya kuruluşlardan ilk mail adresiyle beraber alınmasını &ouml;neririz. Yoksa hesabın ilk sahibi hesabınızı elinizden geri alabilir. &nbsp;Hesabın &Ccedil;alınma durumlarında ilk mail sizdeyse, şifre sıfırlama talebiniz 48 saat i&ccedil;erisinde gelmektedir. İlk mail kısmı halledildikten sonra kesinlikle ikinci &ouml;nemli unsur şifrenizdir. Aldığınız hesabın <strong>şifresini değiştirmeniz gerekmektedir</strong>, ve sonrasında<strong> tweetler beğeniler yapılmış ise kontrol edilmesi</strong> olası bir ihlal s&ouml;z konusu olmamalıdır. <strong>Hesabın daha &ouml;nce politika ihlali yapılmamış ve kısıtlama getirilmemiş olması</strong> burda en &ouml;nemli unsurdur. Son adım olarak dilerseniz şifreniz gibi kullanıcı adınız(@ ile başlayan kısım)&nbsp;veya adınızıda değiştirebilir ve hesabı kendinize g&ouml;re optimize edebilirsiniz.Yazımızın sonuna geldik satın aldığınız Twitter hesabınızda b&uuml;t&uuml;n kontrolleri yaptıktan sonra hesabınız artık tweet atmaya hazırdır g&uuml;venle hesabınızı kullanabilirsiniz.</span></span></span></p>\r\n', '2020-12-25 16:59:24', 0, 0, '0', 'twitter-hesap-alimi-sirasinda-dikkat-edilmesi-gerekenler', 'twitter hesap al', 'Alacağınız twitter ilk mail olduğundan emin olmanız gerekir.Aynı zamanda alacağınız hesabın ilk sahibi daha önceden başkasına satmış ve satın alan kişi değiştirip hesabı size satabilir.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(8, '689368981_earphones-coffee-mug-phone-with-twitter-app.jpg', 'Twitter Retweet Arttırma Yolları', '<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter &uuml;zerinde hesabı olan kullanıcılar i&ccedil;in en &ouml;nemli &ouml;zelliklerden bir tanesi de fazla sayıda retweet almaktır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter retweet arttırmak</strong> her kullanıcının istediği konuların başında gelir. &Ccedil;&uuml;nk&uuml; paylaşılan i&ccedil;eriğin &ccedil;ok daha fazla kişi ile etkileşime ge&ccedil;tiğini g&ouml;rmek her kullanıcının ilk amacıdır. Bu nedenle kullanıcılar her zaman &ccedil;eşitli y&ouml;ntemler kullanmakta ve arttırma yoluna gitmektedirler. Bu nedenle yapılması gerekenler ve yapılmaması gerekenler bulunmaktadır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Bu yol ve y&ouml;ntemleri uyguladıktan sonra kullanıcılar tarafından daha fazla retweet edilme olasılığı artmaktadır. Tabi ki &ouml;ncelikle yapılması gereken konu iyi bir i&ccedil;erik se&ccedil;mek ve o konuda ilerlemek gerekmektedir.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Daha Fazla Retweet İ&ccedil;in Renkli bir İmajınız Olsun</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Tweetlere g&ouml;rsel eklemek kullanıcılar tarafından &ccedil;ok daha fazla ilgi &ccedil;ekecektir. Yapılan araştırmalar, g&ouml;rseli olan paylaşımların daha fazla tıklandığı belirtilmektedir. Bu nedenle paylaşımlarda g&ouml;rsel paylaşmak &ccedil;ok daha iyi olacaktır.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>İlgi &Ccedil;ekici Grafikler ile Dikkat &Ccedil;ekin</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Retweet alma yolları</strong> i&ccedil;erisinde en &ccedil;ok dikkat &ccedil;ekenlerden bir diğeri de infografik eklemek olacaktır. &Ccedil;&uuml;nk&uuml; bu grafikler ile insanlar kısa bilgiler alabilecek ve devamını g&ouml;rebilmek i&ccedil;in bağlantıya tıklayacaklar. Bu durumda bilgi paylaşmak isteyenler de sizin tweetiniz &uuml;zerinde retweet yaparak bilginin &ccedil;oğalmasını sağlayacaklardır.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Takip&ccedil;ilerin Moralini y&uuml;ksek Tutun</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Takip&ccedil;ilerinizi s&uuml;rekli aynı konular ile boğmayın hatta ağır bir konu hakkında paylaşımlarda bulunuyorsanız arada bir sempatik şeyler paylaşarak takip&ccedil;ilerin ilgisini &ccedil;ekin.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Takip&ccedil;ilerinizin Paylaşmak İsteyecekleri Konuları Paylaşın</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">İnsanlar yeni bir bilgiyi kısa s&uuml;rede &ouml;ğrenmeyi ve paylaşmayı olduk&ccedil;a sever. Bu nedenle paylaşımlarınızı bu şekilde yaparsanız daha fazla ilgi &ccedil;ekecektir. Renkli paylaşımlarda bulunmak ve takip&ccedil;ilerinizi heyecanlandıran i&ccedil;erikler paylaşmak dikkat &ccedil;ekecektir.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Takip&ccedil;ilerinize ve Diğer Kullanıcılara Samimi Olun</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter &uuml;zerinden paylaştığınız t&uuml;m i&ccedil;erikler sadece konularınız ile ilgili olmamalı. Gerektiğinde aileniz ve işiniz ile ilgili paylaşımlarda da bulunun. Sizin robot olmadığınızı ve samimi bir insan olduğunu anlamalarını sağlayın. Hatta bazı durumlarda &ccedil;eşitli tatlı serzenişlerde bulunmak insanların dikkatini daha fazla &ccedil;ekecek ve size olan ilgiyi arttıracaktır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter retweet y&uuml;kseltmek</strong> i&ccedil;in bunların haricinde elbette yollar bulunmaktadır. Ancak ana unsurlar bu konular etrafında toplanmış durumdadır.</span></span></p>\r\n', '2020-12-26 19:15:15', 0, 0, '0', 'twitter-retweet-arttirma-yollari', 'Twitter Retweet Arttırma Yolları', 'Twitter retweet arttırmak her kullanıcının istediği konuların başında gelir. Bu nedenle kullanıcılar her zaman çeşitli yöntemler kullanmakta ve arttırma yoluna gitmektedirler. ', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(9, '1793274330_man-showing-twitter-icon.jpg', 'Twitter’da Yapılmaması Gereken Hatalar', '<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter kullanıcılarının yapmaması gereken hatalar bulunması ile birlikte bu hataların yapılması ciddi imaj kaybına neden olmaktadır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter &uuml;zerinden takip&ccedil;i kazanmak isteyenler i&ccedil;in bug&uuml;ne kadar yapılması gerekenler anlatılmıştır. Ancak yapılmaması gereken bazı konularda bulunmaktadır. &Ouml;zellikle takip&ccedil;i kazanmak i&ccedil;in ya da mevcut takip&ccedil;ilerini kaybetmemek i&ccedil;in<strong> yapılmaması gereken Twitter hataları</strong> bulunmaktadır. Bu kurallar olduk&ccedil;a basit ama yapıldığı takdirde ciddi sonu&ccedil;ları olan konulardır.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Uzun Sıklıklar ile Tweet Atmayın</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter, d&uuml;nyada en hızlı g&uuml;ndemi değişen sosyal medya ortamıdır. Tabiri yerinde bir kullanım ile &uuml;lkelerin sorun yaşamasına neden olabilecek kadar hızlı değişen bir yapısı bulunmaktadır. Bu nedenle bu ortamdan uzak durmamak gerekmektedir. S&uuml;rekli değil ama sık sık i&ccedil;erik paylaşmak sizin ve hesabınız i&ccedil;in iyi olacaktır. Bu s&uuml;re tutarsız ve &ccedil;ok uzun olursa hesabınız beklenen ilgiyi g&ouml;remeyecektir.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>S&uuml;rekli Tweet Atmayın</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">&Ccedil;ok sık i&ccedil;erik paylaşmak her şeyden &ouml;nce hesabınızın spam kategorisine sokabilir. Bu nedenle sık sık değil, ama d&uuml;zenli aralıklar ile tweet atmak sizin ilgi &ccedil;ekmenize yarayacaktır. Bunun yanında kullanıcılar da sıkılabilir ya da sizin spam olduğunuzu d&uuml;ş&uuml;nerek şikayet edebilirler.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Fazla Hastag Kullanmayın</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Hastag kullanımı &ouml;zelikle hedef kitleye ulaşma noktasında olduk&ccedil;a &ouml;nemlidir. Bu nedenle paylaşımlarınızı bu doğrultuda yapmak en doğrusu olacaktır. Ancak burada hedef kitleyi belirlerken hastag kullanımını da abartmamak gerekmektedir. Anlatılmak istenenden &ccedil;ok hastag kullanıcıların dikkatini dağıtacak sizlere olan g&uuml;venini sarsacaktır.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Diğer Ağları Burada Paylaşmayın</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter kullanıcı hataları</strong> i&ccedil;erisinde bir diğeri de diğer platformların paylaşımının burada yapılmasıdır. &Ouml;rnek verilecek olursa farklı bir sosyal medya platformunun i&ccedil;eriğini Twitter &uuml;zerinde paylaşmak olduk&ccedil;a yanlış bir işlem olacaktır.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Uzun Tweetlerden Ka&ccedil;ının</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">İlk &ccedil;ıktığı zamanlarda y&uuml;z kırk karakter olan Twitter yazma uzunluğu, iki y&uuml;z seksen karaktere &ccedil;ıkartılmış durumda. Ancak bu durum t&uuml;m karakterleri kullanma zorunluluğunu ortaya koymamaktadır. Bu nedenle anlaşılır ve kısa i&ccedil;erikler paylaşmak yine sizler i&ccedil;in en doğrusu olacaktır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter takip&ccedil;i arttırma yolları</strong> her ge&ccedil;en g&uuml;n artmakla beraber, bu hatalarında yapılmaması gerekmektedir. Bu nedenle yapılması gerekenleri yaparken, yapılmaması gerekenleri atlamamak en doğrusu olacaktır.</span></span></p>\r\n', '2020-12-26 19:17:40', 0, 0, '0', 'twitterda-yapilmamasi-gereken-hatalar', 'Twitter’da Yapılmaması Gereken Hatalar', ' Özellikle takipçi kazanmak için ya da mevcut takipçilerini kaybetmemek için yapılmaması gereken Twitter hataları bulunmaktadır.Bu kurallar oldukça...', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(10, '825575953_hands-holding-phone-with-twitter-laptop.jpg', 'Twitter Profil Fotoğrafı Ekleme', '<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter profil fotoğrafı ekleme konusu her zaman merak edilen konulardan biri olmakla beraber hesap oluşturulurken ya da sonrasında değiştirme işlemi yapılabilir.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter profil resmi değiştirme</strong> işlemi her kullanıcı i&ccedil;in gerekli işlemlerden bir tanesi olarak dikkat &ccedil;ekmektedir. Bilindiği &uuml;zere profil resmi bir hesabın kimlik resmi vazifesi g&ouml;rmektedir. Bu nedenle her kullanıcı her şeyden &ouml;nce profil resmine bakmaktadır. Bu nedenle her hesap sahibi i&ccedil;in olduk&ccedil;a &ouml;nemli bir konudur.</span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Ancak bazı zamanlarda bu işlemin nasıl yapılacağı konusunda kullanıcıların soruları olmaktadır. &Ccedil;&uuml;nk&uuml; Twitter hemen her platformda olan bir uygulamadır ve değişik kullanım şekilleri bulunmaktadır. Bu nedenle kullanıcılar kendi platformları i&ccedil;in olan uygulamaları merak etmektedirler.</span></span></p>\r\n\r\n<h2><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Profil Resmi Nasıl Değişir?</strong></span></span></h2>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter profil resmi nasıl değişir</strong> sorusunun hemen her platform i&ccedil;in tek bir cevabı bulunmaktadır. Bu nedenle profil resmi değiştirme işlemi şu şekilde yapılmaktadır.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Hangi platformdan gireceksiniz &ouml;ncelikle hesabınızı a&ccedil;ın.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Profil sayfanıza giriş yapın.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Profil sayfasında &ccedil;eşitli d&uuml;zenlemeler yapılabilmektedir.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Profil ya da afiş resmini değiştirmek i&ccedil;in kamera simgesine tıklayın</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kamera simgesine tıkladıktan sonra fotoğraf y&uuml;kle veya kaldır butonuna tıklayın.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Sonrasında kaydet butonuna basarak resmin kayıt edilmesini sağlayabilirsiniz.</span></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Bunların yanında profil sayfasında &ccedil;eşitli işlemler yapılabilmektedir. Bunlardan bazıları da şudur.</span></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Afiş resmi i&ccedil;in tavsiye edilen boyutlar 1500x1500 olarak belirlenmiştir.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Profil sayfasında bu fotoğrafın değişikliği de yapılabilmektedir.</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kullanıcı adı değişikliği</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kendiniz ile ilgili kişisel bilgiler</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Hesabınıza ait hesap bilgileri</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Var ise internet siteniz</span></span></li>\r\n	<li><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Size ait doğum tarihi</span></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Profil Resmi Neden &Ouml;nemlidir?</strong></span></span></h2>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter profil fotoğrafı y&uuml;kleme</strong> işlemi hesaplar i&ccedil;in olduk&ccedil;a &ouml;nemlidir. &Ccedil;&uuml;nk&uuml; t&uuml;m kullanıcılar ilk bu resme bakmaktadır. Bu nedenle belirli aralıklarda değiştirmek ve sizi iyi tanımlayan resimler eklemek hesabınız i&ccedil;in olduk&ccedil;a iyi olacaktır. Değiştirme işlemi kolay ve hızlı bir şekilde yapılmasına rağmen iyi se&ccedil;ilmeyen bir profil resmi takip&ccedil;iler ve diğer kullanıcılar i&ccedil;in olduk&ccedil;a &ouml;nemlidir. </span></span></p>\r\n\r\n<p><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Bu sebepler doğrultusunda profil resmine dikkat etmek gerekmekte ve belirtilen y&ouml;ntemler ile birlikte belirli aralıklarla değiştirmekte yarar vardır. Ancak dikkat edilmesi gereken belirtilen boyutlarda y&uuml;klenmesi de olduk&ccedil;a &ouml;nemlidir.</span></span></p>\r\n', '2020-12-26 19:19:44', 0, 0, '0', 'twitter-profil-fotografi-ekleme', 'Twitter Profil Fotoğrafı Ekleme', 'Twitter profil resmi nasıl değişir sorusunun hemen her platform için tek bir cevabı bulunmaktadır. Bu nedenle profil resmi değiştirme işlemi şu şekilde yapılmaktadır.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL);
INSERT INTO `yazilar` (`yazi_id`, `yazi_foto`, `yazi_title`, `yazi_icerik`, `yazi_tarih`, `yazi_kategori_id`, `yazi_kategori_alt_id`, `yazi_okunma`, `yazi_seolink`, `yazi_keyw`, `yazi_desc`, `created_at`, `updated_at`, `deleted_at`) VALUES
(11, '1547665961_woman-showing-twitter-icon.jpg', 'Twitter Takipçi Arttırma Yolları', '<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">En pop&uuml;ler sosyal medya platformlarından biri olan Twitter takip&ccedil;i arttırma yolları, kullanıcılar tarafından en &ccedil;ok araştırılan konuların başında gelmektedir.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter her ge&ccedil;en g&uuml;n kullanıcı sayısını arttıran bir platform olarak &ouml;n plana &ccedil;ıkmaktadır. Bunun yanında artan kullanım sayısı, her kullanıcının daha fazla takip&ccedil;i kazanma isteğini de beraberinde getirmektedir. Ancak bunun i&ccedil;in bir&ccedil;ok hile olsa da bazı zamanlarda bu durum işe yaramamaktadır. Bu nedenle organik olarak arttırmak en doğru yol olacaktır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter takip&ccedil;i arttırma yolları</strong> olduk&ccedil;a fazladır. Ancak bug&uuml;ne kadar denenen yolların bazıları tam sonu. Vermiştir ve vermektedir. Bu nedenle her şeyi denemek yerine sizin i&ccedil;in uygun olanları denemek en doğrusu olacaktır.</span></span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter Takip&ccedil;i Arttırma Yolları Nelerdir?</strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">&Ouml;ncelikle takip&ccedil;ilerin merak ettikleri konularda sık aralıklar ile tweet atmak &ouml;nemlidir. Ancak s&uuml;rekli atmak yerine belirli aralıklarla atmak gerekmektedir. Anlatılması gereken bir konu var ise flood şeklinde yapılması en doğrusu olacaktır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Diğer bir y&ouml;ntem olarak takip edilmek i&ccedil;in takip etmek ortaya &ccedil;ıkmaktadır. <strong>Takibe takip</strong> olarak da bilinen bu y&ouml;ntem ile ne kadar &ccedil;ok kişiyi takip ederseniz o kadar kişi de sizi takip edecektir. Bu şekilde y&uuml;ksek miktarda takip&ccedil;i sayısına ulaşabilirsiniz. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">&Ouml;zellikle işletme hesapları i&ccedil;in, kullanıcıların geri d&ouml;n&uuml;şleri olduk&ccedil;a &ouml;nem taşımaktadır. İşletmenizin tanımını yapan anahtar kelimeler ile arama yaparak, yapılan olumlu eleştirilere teşekk&uuml;r ederken, şikayetler ile ilgili &ccedil;&ouml;z&uuml;m sunmak kullanıcıların g&ouml;nl&uuml;n&uuml; kazanacaktır. Bu nedenle bu şekilde aramalar yapmak sizin ve işletmenizin prestijini arttıracaktır.</span></span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:24px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Doğal Hayata Uygun Tweetler Atın</strong></span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Kalıp halinde tweetler atarak kullanıcıların dikkatini &ccedil;ekmeniz m&uuml;mk&uuml;n değildir. &Ouml;zellikle işletmeler bu konuda daha dikkatli davranmalıdır. &Ccedil;&uuml;nk&uuml; her ne kadar resmi olsa da atılacak tweetlerde markanın canlı bir organizma olduğu yansıtılmalıdır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Bunların yanında s&uuml;rekli aynı şekilde paylaşımlar yapmaktan da ka&ccedil;ınmalısınız. &Ccedil;&uuml;nk&uuml; s&uuml;rekli aynı zamanlarda aynı şekilde tweet atmak merak duygusunu kaybettirecektir. &Ouml;rneğin her akşam 20&rsquo;de bir tweet atıyor ve aynı konuya dair paylaşımlar yapıyorsanız, arada bir kendiniz ile ilgili de bir şeyler paylaşmak takip&ccedil;ilerde merak uyandırabilir.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter takip&ccedil;i arttırma</strong> yollarından bir tanesi de etiketlerden yararlanmaktır. Etiketler konuların yoğun olarak tartışıldığı alanlardır. Bu alanlarda yerinde ve zamanında paylaşımlar yapmak sizlere takip&ccedil;i arttıracaktır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Yine diğer bir takip&ccedil;i arttıracak y&ouml;ntem ise sabitlenmiş tweettir. &Ccedil;&uuml;nk&uuml; ilgi &ccedil;eken bir tweet sonrasında profilinize gelen kullanıcılar &ouml;ncelikle sabitlenmiş tweet ile karşılaşmaktadır. Bu nedenle en iyi olduğuna inandığınız tweetinizi buraya sabitlemeniz sizin i&ccedil;in iyi olacaktır. Ancak burada dikkat edilmesi gereken konu s&uuml;rekli aynı konuyu sabit tutmak takip&ccedil;i kaybına neden olacaktır.</span></span></p>\r\n', '2020-12-27 10:56:15', 0, 0, '0', 'twitter-takipci-arttirma-yollari', 'Twitter Takipçi Arttırma Yolları', 'Diğer bir yöntem olarak takip edilmek için takip etmek ortaya çıkmaktadır. Takibe takip olarak da bilinen bu yöntem ile ne kadar çok kişiyi takip ederseniz o kadar kişi de sizi takip edecektir...', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(12, '2025237007_pile-of-3d-twitter-logos.jpg', 'Twitter’da Tutarlı Marka Oluşturma', '<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">En &ccedil;ok kullanılan sosyal medya platformlarından biri olan Twitter ile markanızın daha iyi yerlere gelmesini sağlayabilirsiniz.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter ile marka imajı oluşturma</strong> t&uuml;m markalar i&ccedil;in olduk&ccedil;a &ouml;nemlidir. &Ccedil;&uuml;nk&uuml; kullanıcılarına karşı tavır ve davranışları markanın g&ouml;r&uuml;nen y&uuml;z&uuml; olmaktadır. Markalar bu imajı oluşturmak i&ccedil;in &ouml;ncelikle hesap a&ccedil;malıdırlar.&nbsp; Ancak sadece hesap a&ccedil;mak iyi bir imaj oluşturmak i&ccedil;in yeterli değildir. Bunun i&ccedil;in &ccedil;eşitli y&ouml;ntemler bulunmaktadır.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Kullanıcı Adınız Değişmemeli</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Firmalar hesap a&ccedil;tıktan sonra belirli konularda asla oynama yapmamalıdır. &Ouml;zellikle kullanıcı ismi değişikliği asla yapılmaması gereken konulardandır. Bu nedenle isim tercihinde bulunurken &ccedil;ok dikkatli olunmalıdır. &Ccedil;&uuml;nk&uuml; kullanıcılarda kurumsal firmaların isimlerini değiştirmeleri kafa karışıklığına neden olabilmektedir. Bu nedenle kullanıcı adı &ccedil;ok elzem bir durum dışında değişmemelidir.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>İ&ccedil;eriklerinizi Belirli Bir Plana G&ouml;re Belirleyin</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Paylaşımlarınız markanızın kimliğini oluşturmaktadır. &Ccedil;&uuml;nk&uuml; kullanıcılar sizin web siteniz ya da hizmet verdiğiniz yerlerden &ouml;nce sosyal medya hesaplarınızı g&ouml;rebilirler. Bu nedenle iyi bir strateji izlemek ve ona g&ouml;re paylaşımlarda bulunmak en doğrusu olacaktır. </span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Bunun yanında g&uuml;ndemi de takip etmek gerekir. &Ccedil;&uuml;nk&uuml; toplumun değer verdiği konulara &ouml;nem vermek kullanıcıların ilgisini &ccedil;ekecek ve imajınızı olumlu y&ouml;nde geliştirecektir. Bunun yanında toplumun hassas olduğu konularda da dikkatli olmak ve ona g&ouml;re paylaşım yapmak hatta belki de o konuya değinmemek en doğrusu olacaktır.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Retweet Edilen ve Favorilere Eklenenler Konusunda Dikkatli Olun</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter &uuml;zerinde retweet ya da favorilere eklemek her zaman markanız i&ccedil;in iyi olmamaktadır. Bu nedenle buna &ccedil;ok dikkat etmek ve &ouml;zenli davranmak gerekmektedir. Toplumun hassas olduğu konuları i&ccedil;eren işlemler bazı zamanlardan olumsuz etki ortaya &ccedil;ıkarabilmektedir. <strong>Twitter işletme hesapları</strong> i&ccedil;in her zaman iyi sonu&ccedil;lar &ccedil;ıkarmayabilir.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Mentionlara Dikkat Edin</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Diğer işlemlerde olduğu gibi, bu konuda da dikkatli olmak gerekmektedir. M&uuml;şterileriniz veya ortaklarınız ile girdiğiniz diyaloglar olumlu sonu&ccedil;lar verebilir. Hatta rakip olan veya sizi eleştiren kişilerle samimi ama seviyeli ve hak verir atışmalar sempati arttırır. Ancak gereksiz tartışmalar &ccedil;ok fazla imaj kaybına yol a&ccedil;ar.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>D&uuml;zenli Aralıklar ile Tweet Atın</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Twitter ile imaj y&uuml;kseltme</strong> işlemlerinin en &ouml;nemlilerinden biri de d&uuml;zenli aralıklar ile yapılan paylaşımlardır. Belirli olmayan aralıklar ile yapılan paylaşımlar nedeniyle kullanıcıların ilgisi azalacak ve giderek imaj kaybı yaşayacaksınız.</span></span></p>\r\n', '2020-12-27 10:58:26', 0, 0, '0', 'twitterda-tutarli-marka-olusturma', 'Twitter’da Tutarlı Marka Oluşturma', 'En çok kullanılan sosyal medya platformlarından biri olan Twitter ile markanızın daha iyi yerlere gelmesini sağlayabilirsiniz.Twitter ile marka imajı oluşturma tüm markalar için oldukça önemlidir.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(13, '2034449420_well-known-social-media-brands-printed-on-paper-arranged-near-digital-tablet-and-smartphone.jpg', 'Twitter Bilinmesi Gereken Özellikler', '<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">En &ccedil;ok kullanılan sosyal medya platformlarında biri olan Twitter bilinmesi gereken &ouml;zellikleri ile de dikkat &ccedil;ekmektedir.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">D&uuml;nya &uuml;zerinde en &ccedil;ok kullanıcısı olan sosyal medya platformlarından biri olan <strong>Twitter bilinmesi gereken &ouml;zellikler</strong> ile de en &ccedil;ok kullanılan platformlardan biri olmaktadır. Kullanım kolaylığı ve kolay paylaşımları ile kullanıcıların dikkatini &ccedil;eken Twitter ile birlikte aynı anda tanımadığınız milyonlarca insan ile etkileşime girebilirsiniz. Bunun yanında bilinmesi gereken &ouml;zellikle ise fazladır. </span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Gelen Cevapları Saklayın</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Attığınız tweetlere gelen cevapları saklayabilirsiniz. Başka bir sayfaya taşınan cevaplar, i&ccedil;eriğinizin altında g&ouml;r&uuml;nmeyecektir. Ancak belirtilen işaret ile dilerse kullanıcı gizli cevabı g&ouml;rmek i&ccedil;in diğer sayfaya gidebilir.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Karanlık Mod Se&ccedil;enekleri Artıyor</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Twitter karanlık modu ve aydınlık moduna <strong>Dim modu</strong> eklenmiş durumdadır. Bu mod ile daha farklı bir mod eklenmiş durumdadır.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Eski Twetlerinizi Kolaylıkla Bulun</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Daha &ouml;nce attığınız ya da kullanıcıda var olan ama şu anda hemen bulmanız gereken tweet var ise yapmanız gereken olduk&ccedil;a kolay. @kullanıcı adı &ldquo;#aranansey&rdquo; şeklinde arama yaparak bulabilirsiniz. </span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Anketler ile Araştırmalar Yapın</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Bazı şirketler &ccedil;eşitli araştırmalar yapmak i&ccedil;in Twitter anket se&ccedil;eneği sunmaktadır. Bu se&ccedil;enek ile bilmek istediğiniz konuda araştırma yapabilir ve kullanıcıların ne d&uuml;ş&uuml;nd&uuml;ğ&uuml;n&uuml; &ouml;ğrenebilirsiniz. Bu nedenle bu t&uuml;r araştırmalar yapmak Twitter ile m&uuml;mk&uuml;n.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Sonradan Tweetleyin</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Like&rsquo;lamak istediğiniz ama sizlerin bunu yapmasının g&ouml;r&uuml;nmesini istemediğiniz onlarca tweet bulunmaktadır. Bunun i&ccedil;in yer imleri bulunmaktadır. Bunlar ile birlikte sevdiğiniz i&ccedil;eriklere sonradan bakabilir ve kayıt edebilirsiniz. Bu imleri yalnız ama yalnız siz g&ouml;receksiniz. B&ouml;ylece sizin tarafınızdan beğenildiği bilinmeyecek ama sizlerin de istediği anda g&ouml;rebileceği paylaşımları olacak.</span></span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:22px\"><span style=\"font-family:Calibri,sans-serif\"><strong>Profile Sabitleyin</strong></span></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">Herkesin g&ouml;rmesini istediğiniz ancak zaman akışı i&ccedil;inde kayıp gitmesini istemediğiniz paylaşımlarınızı profilinize sabitleyebilirsiniz. <strong>Sabitlenmiş tweet</strong> ile herkesin profilinize girdiğinde g&ouml;rebileceği i&ccedil;erikleriniz olabilir. Ancak takip&ccedil;i kazanmak istiyor ya da takip&ccedil;ilerinizi sıkmak istemiyor iseniz sabitlenmiş tweetleri belirli aralıklar ile değiştirmekte fayda vardır.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\"><span style=\"font-family:Calibri,sans-serif\">T&uuml;m bu &ouml;zellikler ve daha fazlası her ge&ccedil;en g&uuml;n artan kullanıcı sayısı ile Twitter&rsquo;in kullanılma sebepleri arasındadır. Ancak bu &ouml;zelliklerden daha fazlası ve daha ilgi &ccedil;ekenleri sosyal medya platformunda sunulmuş durumdadır.</span></span></p>\r\n', '2020-12-27 11:01:15', 0, 0, '0', 'twitter-bilinmesi-gereken-ozellikler', 'Twitter Bilinmesi Gereken Özellikler', 'Twitter bilinmesi gereken özellikler ile de en çok kullanılan platformlardan biri olmaktadır. Kullanım kolaylığı ve kolay paylaşımları ile kullanıcıların dikkatini çeken Twitter ile birlikte aynı anda ', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(14, '1095618842_eraherhearrhne.jpg', 'Twitter Retweet Nedir? Nasıl Yapılır?', '<p><span style=\"font-size:20px\">Twitter &uuml;zerinden paylaşılan bir tweeti olduğu gibi almak i&ccedil;in yapılan işleme retweet denirken, yapılması da olduk&ccedil;a kolaydır.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Retweet nedir&nbsp;sorusunun basit bir cevabı bulunmaktadır. Bir kullanıcının yaptığı bir paylaşımı olduğu gibi, kendi d&uuml;ş&uuml;nceleriniz ve eklemeniz olmadan yeninden paylaşmaktır. &Ouml;rneğin &uuml;nl&uuml; bir oyuncunun yaptığı bir paylaşımı paylaşmak ve kendi takip&ccedil;ilerinize olduğu gibi g&ouml;stermek istediğinizde bu işlemi yapabilirsiniz.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">&Ouml;zellikle takip&ccedil;i sayısının artmasını isteyenlerin ya da kendi&nbsp;twettlerinin&nbsp;daha fazla insana ulaşmasını isteyen kullanıcılar bu durumu olduk&ccedil;a fazla &ouml;nemsemektedir. &Ccedil;&uuml;nk&uuml; bu durumda daha fazla insana ulaşma&nbsp;imkanı&nbsp;bulunmaktadır. Bu durumda da d&uuml;ş&uuml;ncelerin daha fazla insana ulaşması m&uuml;mk&uuml;n olmaktadır.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:24px\">Nasıl Retweet Edilir?</span></strong></h3>\r\n\r\n<p><span style=\"font-size:20px\">Retweet adından da anlaşılacağı &uuml;zere, bir tweeti yeniden&nbsp;tweetlemek&nbsp;demektir.&nbsp;Bir&ccedil;ok kullanıcı&nbsp;retweet nasıl yapılır sorusunu merak etmektedir. İlk kurulduğu zamanlarda daha farklı olsa da şu anda basit bir y&ouml;ntem ile yeniden paylaşmak m&uuml;mk&uuml;n hale gelmektedir.&nbsp;Retweet yapmak&nbsp;ise olduk&ccedil;a kolaydır.&nbsp;Sadece yeniden iletmek istediğiniz paylaşımın &uuml;zerinde retweet butonuna basmanız yeterli olacaktır.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">&bull;&nbsp;&Ouml;ncelikle paylaşmak istediğiniz tweetin &uuml;st&uuml;ne gelin.<br />\r\n&bull;&nbsp;Retweet butonuna basın.<br />\r\n&bull;&nbsp;Yeni a&ccedil;ılan pencerede retweetlemek istediğiniz tweet a&ccedil;ılır.<br />\r\n&bull;&nbsp;Burada retweet butonuna basın.&nbsp;<br />\r\nBu yol ile herhangi bir tweeti değiştirmeden retweet edebilirsiniz.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:24px\">Retweet Yapılan Bir Tweeti Geri Alma</span></strong></h3>\r\n\r\n<p><span style=\"font-size:20px\">Bazı durumlarda&nbsp;retweet geri&nbsp;alma işlemi&nbsp;yapmak&nbsp;istenebilir. Bunun &ccedil;eşitli sebepleri bulunmaktadır. Bu durumda olduk&ccedil;a kolaydır.<br />\r\n&bull;&nbsp;Retweet edilen tweetin &uuml;zerine gelin.<br />\r\n&bull;&nbsp;Retweet butonuna tıklayın.<br />\r\n&bull;&nbsp;Bu işlemi yaptıktan sonra hem tweet sizin profil zaman akışınızdan kalkacak hem de tweeti retweet edenler kısmından sizin hesabınız kaldırılacaktır.<br />\r\nBunun yanında &ouml;nemli bir notu belirtmekte yarar vardır.<strong>satılık twitter hesapları</strong> Retweet geri alındığında t&uuml;m zaman akışlarından kaldırılır. Ancak &ouml;nbelleğe alınan zaman akışlarını g&ouml;r&uuml;nt&uuml;leyen mobil ciharlarda hala g&ouml;r&uuml;nmeye devam edecektir.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:24px\">Tweeti Alıntı Yapma</span></strong></h3>\r\n\r\n<p><span style=\"font-size:20px\">&bull;&nbsp;Alıntılama yapmak istediğiniz tweetin &uuml;zerine gidin.<br />\r\n&bull;&nbsp;Retweet butonuna tıklanır.<br />\r\n&bull;&nbsp;Alıntı Tweet Olarak&nbsp;Retweetle&nbsp;butonuna basılır.<br />\r\n&bull;&nbsp;Burada normal bir tweet atar gibi yorum yazabilir ve resim paylaşabilirsiniz.<br />\r\nRetweet alıntılama durumunda kullanıcıların da yorumları bulunmakta ve ister onaylama i&ccedil;in ister de cevap i&ccedil;in kullanılır.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:24px\">Neden Bazı Tweetler Retweetlenemiyor?</span></strong></h3>\r\n\r\n<p><span style=\"font-size:20px\"><strong>Twitter satılık hesap</strong>.&nbsp;&uuml;zerinden t&uuml;m tweetler retweet edilemez. Birinin profilinde veya kullanıcı adının yanında kilit simgesi g&ouml;r&uuml;n&uuml;yorsa bu kişinin tweetleri koruma altındadır. Bu durumda bu kişinin tweetlerini retweet yapmanız m&uuml;mk&uuml;n değildir. Bununla birlikte takip istediğinizi kabul eden birinin t&uuml;m tweetlerini zaman akışınızda veya bu kişinin profilinde g&ouml;rebilirsiniz. Bu kişinin tweetlerini korumaya alması durumunda ise siz ya da başka biri bu kişinin tweetlerini retweetleyemez.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Kendi hesabınızda tweetleriniz korumalı olsa dahi kendi atmış olduğunuz tweetleri retweetleyebilirsiniz. Aynı zamanda yorum ekleyerek te retweet yapmanız m&uuml;mk&uuml;nd&uuml;r. Yaptığınız yorumları ve retweetlerinizi yalnızca sizi takip eden kişiler g&ouml;rebilecektir.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Retweet Kapatma Nasıl Yapılır?</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Bir kullanıcının sayfasında yer alan retweetleri g&ouml;rmek istemiyorsanız, bunları kaldırmanız m&uuml;mk&uuml;nd&uuml;r. Hesabın paylaşmış olduğu i&ccedil;erikleri beğenmiyorsanız yapmanız gereken retweetleri kapatmaktır. &nbsp;Kullanıcının retweetlemiş olduğu tweetleri kapatmak i&ccedil;in birka&ccedil; adım izleyerek kısa s&uuml;rede bu işlemi ger&ccedil;ekleştirmeniz m&uuml;mk&uuml;nd&uuml;r.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Bu kişinin profil sayfanıza girdikten sonra retweet kapat se&ccedil;eneğini tıklamanız gerekmektedir.</span><br />\r\n<span style=\"font-size:20px\">Android cihazdan twitter hesabına giriş yapmışsanız taşma simgesine dokunmanız yeterlidir. İOS cihazdan giriş yapmışsanız o halde ayarlar simgesine tıklayın. Buraya tıkladıktan sonra retweet kapatmış olursunuz. Kapatma işlemini yaptıktan sonra retweet kapatma ya da a&ccedil;ma işlemi geriye d&ouml;n&uuml;k giden bir işlem değildir. Retweet kapatma ya da a&ccedil;ma işlemini yaptıktan sonraki retweetler g&ouml;r&uuml;necek ya da g&ouml;r&uuml;nmeyecektir.&nbsp;</span></p>\r\n', '2020-12-30 19:28:24', 0, 0, '0', 'Twitter-Retweet-Nedir-Nasil-Yapilir', 'Twitter,Retweet,Nedir,Nasil,Yapilir', 'Twitter üzerinden paylaşılan bir tweeti olduğu gibi almak için yapılan işleme retweet denirken, yapılması da oldukça kolaydır.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(15, '2129524762_gwegwegwe.jpg', 'Twitter Devre Dışı Bırakılan Hesabı Açma', '<p><span style=\"font-size:20px\">&Ccedil;eşitli sebeplerle silinmek istenen hesapların yeniden aktif edilmesi ve Twitter devre dışı bırakılan hesabı a&ccedil;ma işlemlerinin yapılması m&uuml;mk&uuml;nd&uuml;r</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Twitter g&uuml;n&uuml;m&uuml;zde en &ccedil;ok kullanılan sosyal medya platformlarında biri olarak karşımıza &ccedil;ıkmaktadır. Bu durum belirli sorunları da beraberinde getirmektedir. &Ouml;ncelikle g&uuml;venlik soruları ile kullanıcıların kafasında b&uuml;y&uuml;k bir soru işareti bulunmaktadır. Bu noktada bir&ccedil;ok kullanıcı&nbsp;<strong>Twitter hesabını kalıcı olarak</strong> kapatma&nbsp;işlemi yapmak istemektedirler. Ya da bunun gibi bir&ccedil;ok sebep nedeniyle kalıcı olarak hesap silinmek istenmektedir.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Elbette bu m&uuml;mk&uuml;n bir işlemdir. Ancak bunun i&ccedil;in yaklaşık 30 g&uuml;n kadar beklemek gerekmektedir. Kapatma işleminin ardından 30 g&uuml;n sonra t&uuml;m verileriniz kalıcı olarak silinmekte ve bir daha geri gelmemektedir. Bu durumda yapılacak olan ise yeni bir hesap a&ccedil;mak olacaktır.</span></p>\r\n\r\n<p><strong><span style=\"font-size:20px\">Twitter Hesabı Nasıl Devre Dışı Bırakılır?</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Twitter hesabınızı belirli nedenlerden dolayı devre dışı bırakmak isteyebilirsiniz. <strong>twitter Hesap Satın Al</strong></span><br />\r\n<span style=\"font-size:20px\">Bu durumda yapmanız gereken belirli adımları takip ederek hesabınızı devre dışı bırakmaktır. Birka&ccedil; adımda kolay bir şekilde hesabınızı devre dışı bırakabilir, belirlenen s&uuml;re i&ccedil;erisinde hesabınızı tekrar a&ccedil;abilirsiniz. Twitter&rsquo;da kullanıcı adınızı değiştirmek i&ccedil;in ya da e-posta adresinizi değiştirmek i&ccedil;in hesabınızı devre dışı bırakmanıza gerek yoktur. Bunları hesap ayarlarından rahatlıkla değiştirebilirsiniz. Hesabınızı devre dışı bıraktıktan sonra size dair kullanıcı adınız, hesabınız veya herhangi bir bilginiz Twitter&rsquo;da g&ouml;r&uuml;nt&uuml;lenmeyecektir. Hesabınızı hatalı veya isteyerek devre dışı bıraktıktan sonra 30 g&uuml;n i&ccedil;inde hesabınızı geri getirme şansınız bulunmaktadır.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Twitter hesabınızı devre dışı bırakmak i&ccedil;in yapmanız gerekenler şunlardır;<br />\r\nİlk olarak hesabınıza giriş yapın.&nbsp;<br />\r\nHesabınıza giriş yaptıktan sonra profil simgenizin altında yer alan Ayarlar ve gizlilik sekmesine tıklamalısınız.<br />\r\nKarşınıza &ccedil;ıkan hesap sekmesinde dokunduğunuzda <strong>Hesabımı devre dışı bırak</strong> se&ccedil;eneği gelecektir. Bu durumda yapmanız gereken Hesabınızı devre dışı bırakmaya dair bilgileri okumaktır. T&uuml;m bilgileri okuduktan sonra hesabınızı devre dışı bırakmak konusunda hala kararlıysanız Kullanıcı adını devre ışı bırak se&ccedil;eneğine tıklamanız gerekmektedir.&nbsp;<br />\r\nBu se&ccedil;eneğe tıkladıktan sonra karşınıza twitter bilgileriniz gelecektir. Burada hesap şifrenizi doğru olarak girmeniz gerekmektedir. Şifrenizi doğru bir şekilde girdikten sonra hesabımı devre dışı bırak se&ccedil;eneğine tıklayabilirsiniz. Eğer vazge&ccedil;mişseniz iptal butonuna tıklayabilirsiniz.&nbsp;</span></p>\r\n\r\n<h3><strong><span style=\"font-size:24px\">Dondurulan Hesabı Geri Getirme</span></strong><br />\r\n&nbsp;</h3>\r\n\r\n<p><span style=\"font-size:20px\">Twitter hesap silme işlemini hemen devreye koymamaktadır. &Ouml;ncelikle 30 g&uuml;n beklemekte ve daha sonra kalıcı olarak silmektedir. Ancak bu 30 g&uuml;n i&ccedil;erisinde yeniden aynı hesap bilgileri ile giriş yapılırsa hesabınız yeniden aktif hale gelecektir. Bu durumda&nbsp;<strong>Twitter dondurulan hesap geri getirme</strong>&nbsp;m&uuml;mk&uuml;n olacaktır.&nbsp;Bu s&uuml;re aşıldığı takdirde bir daha hesabın geri getirilmesi m&uuml;mk&uuml;n olmayacaktır.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Ancak bu s&uuml;renin iyi anlaşılması gerekmektedir. Aksi takdirde hesap bir daha geri getirilemeyecektir. Bu durum karşısında ise yapılacak tek bir işlem bulunmaktadır. O da yeni bir hesap a&ccedil;mak olacaktır. &Ccedil;&uuml;nk&uuml; tamamen kapatılan bir hesap asla geri gelmemektedir.</span></p>\r\n\r\n<h3><strong><span style=\"font-size:24px\">Tamamen Kapatılan Hesap Aktif Edilebilir mi?</span></strong></h3>\r\n\r\n<p><span style=\"font-size:20px\">Hesabınızı kapatma işlemi yaptıktan sonra 30 g&uuml;n i&ccedil;erisinde&nbsp;tekrar hesabınıza girmeniz durumunda hesabınız tekrar aktif hale gelebilmektedir. Bu durum ile kapattığınız hesabınızın tekrar a&ccedil;ılmasını sağlayabilirsiniz.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Tamamen&nbsp;<strong>kapatılan Twitter hesabını tekrar aktif hale getirme</strong>&nbsp;elbette 30 g&uuml;nl&uuml;k s&uuml;re i&ccedil;erisinde m&uuml;mk&uuml;nd&uuml;r. Ancak bu s&uuml;re ge&ccedil;ildikten sonra hesabın tekrar geri getirilmesi m&uuml;mk&uuml;n olmamaktadır. Bu nedenle hesabı kapatmadan &ouml;nce iyi d&uuml;ş&uuml;nmek ve ona g&ouml;re işlem yapılmalıdır.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Yeniden aktif etme işlemi bu şartlarda olmaktadır. Ancak s&uuml;renin ge&ccedil;memesi en &ouml;nemli konuların başında gelmektedir. Aksi takdirde hesabın yeninden getirilmesi m&uuml;mk&uuml;n olmamaktadır.</span><br />\r\n&nbsp;</p>\r\n', '2020-12-30 19:55:32', 0, 0, '0', 'Twitter-Devre-Disi-Birakilan-Hesabi-Acma', 'Twitter,Devre,Dişi,Birakilan,Hesabi,Açma', 'Twitter hesabınızı devre dışı bırakmak için yapmanız gerekenler şunlardır; İlk olarak hesabınıza giriş yapın.  Hesabınıza giriş yaptıktan sonra profil simgenizin altında yer alan Ayarlar ve gizlilik sekmesine tıklamalısınız.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(16, '1695109856_yjtjysdtjst.jpg', 'Twitter Hassas İçerik Ayarı Nasıl Açılır?', '<p><span style=\"font-size:20px\">Twitter uygulamasında bazı sıkıntı oluşturabilecek i&ccedil;eriklerin g&ouml;r&uuml;nt&uuml;lenmesini engellemek i&ccedil;in hassas i&ccedil;erik ayarlaması bulunmaktadır.</span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong>Hassas i&ccedil;erik</strong>, kişileri travmatik olarak etkiyebilecek paylaşımlardır. Kullanıcıların bakmasının uygun olmadığı i&ccedil;erikler hassas i&ccedil;erik olarak karşımıza &ccedil;ıkabilir. Bunlar genellikle +18 cinsel i&ccedil;erikli paylaşımlar, tehlikeli, korkun&ccedil; ya da kan i&ccedil;erikli paylaşımlar olabilmektedir. &Ouml;zellikle k&uuml;&ccedil;&uuml;k &ccedil;ocukları ve hassas kişileri koruma amacıyla yapılan hassas i&ccedil;erik uygulaması, sağlık ve g&uuml;venlik a&ccedil;ısından &ouml;nemlidir. Bundan dolayı twitter uygulaması kullanıcılarına hassas i&ccedil;erikleri kapatma şansı tanımaktadır. Hassas i&ccedil;erik kapatma gibi a&ccedil;ma i&ccedil;in de ayarlarınızı değiştirebilirsiniz.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">İlk kurulduğu g&uuml;nden bu yana s&uuml;rekli g&uuml;ndemde olan Twitter kullanıcıları i&ccedil;in s&uuml;rekli yeni g&uuml;ncellemeler bulunmaktadır. Bunlardan bir tanesi de&nbsp;hassas i&ccedil;erik ayarı&nbsp;olmaktadır. Bu ayar ile birlikte kullanıcılarının daha sağlıklı bir ortamda sosyal medyayı kullanmasını ama&ccedil;lamaktadır. Bu uyarılar şu şekilde karşınıza &ccedil;ıkabilir</span></p>\r\n\r\n<p><span style=\"font-size:20px\">&bull;&nbsp;Bu medya hassas materyaller i&ccedil;erebilir.<br />\r\n&bull;&nbsp;Bu profil potansiyel olarak hassas i&ccedil;erik i&ccedil;erebilir.<br />\r\n&bull;&nbsp;Aşağıdaki medya potansiyel olarak hassas i&ccedil;erik i&ccedil;erebilir.<br />\r\nHassas i&ccedil;erikler bu şekilde karşınıza &ccedil;ıkmaktadır. Ancak bu i&ccedil;erik ayarlarını değiştirmek m&uuml;mk&uuml;nd&uuml;r. Ancak bunun i&ccedil;in &ouml;ncelikle Twitter hesabınız ile oturum a&ccedil;manız gerekmektedir. Yok, ise yeni bir hesap oluşturmanız gerekmektedir.</span></p>\r\n\r\n<h3><span style=\"font-size:24px\"><strong>Hassas İ&ccedil;erik Uyarısı Nasıl&nbsp;A&ccedil;ılır?</strong></span></h3>\r\n\r\n<p><span style=\"font-size:20px\">Hassas i&ccedil;erik uyarısı nasıl&nbsp;a&ccedil;ılır&nbsp;sorusu da en &ccedil;ok merak edilen sorular arasında yerini almaktadır. Bu konuda bu ayarları&nbsp;a&ccedil;mak&nbsp;elbette m&uuml;mk&uuml;nd&uuml;r. Bunun i&ccedil;in &ouml;ncelikle bir Twitter hesabınızın olması gerekmektedir. Yok, ise bile yeni bir hesap a&ccedil;mak gerekmektedir. Hassas i&ccedil;erik uyarısını&nbsp;a&ccedil;mak i&ccedil;in yapılması gerekenler şunlardır.<br />\r\n&bull;&nbsp;Twitter hesabına giriş yapılır.<br />\r\n&bull;&nbsp;Profil resmine tıklanır.<br />\r\n&bull;&nbsp;A&ccedil;ılan pencerede Ayarlar ve Gizlilik butonuna tıklanır.<br />\r\n&bull;&nbsp;Karşınıza &ccedil;ıkan sayfada Hassas İ&ccedil;eriği Gizle se&ccedil;eneği &ccedil;ıkacaktır.<br />\r\n&bull;&nbsp;Bu se&ccedil;eneği işaretlemeniz gerekmektedir.<br />\r\nBu şekilde hassas i&ccedil;erikler ile ilgili uyarılar sizlerin karşısına &ccedil;ıkacaktır. Ancak daha sonra bu uyarıları kapatmak isterseniz yapmanız gereken bu işlemin tersini yapmaktır.</span></p>\r\n\r\n<h3><span style=\"font-size:24px\"><strong>Aramalarda Hassas İ&ccedil;erik G&ouml;sterme</strong></span></h3>\r\n\r\n<p><span style=\"font-size:20px\"><strong>Aramalarda hassas i&ccedil;erik g&ouml;sterme</strong>&nbsp;işlemi ayarlardan kapatılmış ise yapılmaz. Bunun i&ccedil;in yine ayarlardan hassas i&ccedil;eriği g&ouml;ster se&ccedil;eneğinin kaldırılmış olması gerekmektedir.<br />\r\nBu ayarı a&ccedil;mak i&ccedil;in yine hassas i&ccedil;eriği gizle se&ccedil;eneğinin pasif olması gerekmektedir. Ayarlar ve Gizlilik kısmından İ&ccedil;erik Tercihleri se&ccedil;ilmelidir. <strong>satılık eski tarihli twitter hesapları</strong></span><br />\r\n<span style=\"font-size:20px\">Daha sonrasında Arama ayarlarına tıklanır. Aramalarda hassas i&ccedil;erik g&ouml;sterilmesi şu şekilde yapılır.<br />\r\n&bull;&nbsp;Twitter hesabına giriş yapılır.<br />\r\n&bull;&nbsp;Profil &uuml;zerine tıklanır&nbsp;<br />\r\n&bull;&nbsp;Ayarlar ve Gizlilik kısmına gidilir.<br />\r\n&bull;&nbsp;İ&ccedil;erik Tercihleri se&ccedil;ilir.<br />\r\n&bull;&nbsp;Arama Ayarlarına tıklanır.<br />\r\n&bull;&nbsp;Daha sonrasında burada bulunan Hassas İ&ccedil;eriği Gizle se&ccedil;eneği kaldırılır.&nbsp;<br />\r\nBu işlemler ile birlikte hassas i&ccedil;eriklerin aramalarda karşınıza &ccedil;ıkması sağlanır. Yine kapatmak i&ccedil;in yapmanız gereken bu işlemin tersidir.</span></p>\r\n\r\n<h3><span style=\"font-size:24px\"><strong>Hassas İ&ccedil;erik Ayarı Nasıl Değiştirilebilir?</strong></span></h3>\r\n\r\n<p><span style=\"font-size:20px\">Twitter kullanırken bazı tweetlerde karşınıza hassas i&ccedil;erik uyarısı &ccedil;ıkabilmektedir. Bu tweetleri g&ouml;r&uuml;nt&uuml;lemek i&ccedil;in hassas i&ccedil;erik ayarınızı değiştirmeniz gerekmektedir. Hassas i&ccedil;erik ayarını değiştirmek i&ccedil;in yapmanız gereken profilinize giriş yapmaktır. Profilinizdeki Ayarlar ve Gizlilik &ouml;gesine tıklayarak daha sonra karşınıza gelecek olan gizlilik ve g&uuml;venlik &ouml;gesine tıklamanız gerekmektedir. Gereken adımları takip ettikten sonra hassas i&ccedil;erik ayarınızı değiştirmiş olursunuz.<br />\r\nHassas i&ccedil;erik ayarınızı buradan değiştiremediyseniz o halde yapmanız gereken, tarayıcınızdan twittera giriş yapmaktır. Twitterda yer alan tweet medya i&ccedil;eriği &ouml;gesine tıklayabilir karşınıza &ccedil;ıkan hassas i&ccedil;eriğe sahip medyaları g&ouml;r&uuml;nt&uuml;le kutusuna tıklayabilirsiniz. B&ouml;ylece hassas i&ccedil;erik bulunan paylaşımları artık g&ouml;rebileceksiniz. Aynı zamanda twitter ana sayfada karşınıza sık sık &ccedil;ıkan hassas i&ccedil;erik yazısını da g&ouml;rmemiş olursunuz. <strong>Hassas i&ccedil;erik değiştirme</strong> kullanıcıların mobil cihazlardan veya tarayıcı &uuml;zerinden kolaylıkla yapabilecekleri bir işlemdir.&nbsp;</span></p>\r\n', '2020-12-31 18:02:46', 0, 0, '0', 'twitter-hassas-icerik-ayari-nasil-acilir', 'Twitter,Hassas,İçerik,Ayari,Nasil,açilir', 'Hassas içerik, kişileri travmatik olarak etkiyebilecek paylaşımlardır. Kullanıcıların bakmasının uygun olmadığı içerikler hassas içerik olarak karşımıza çıkabilir.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(17, '8228217_srtjjrstjsr.jpg', 'Twitter Çekilişi Nedir Nasıl Yapılır?', '<p><span style=\"font-size:20px\">&Ccedil;eşitli&nbsp;adımları&nbsp;kullanarak&nbsp;<strong>twitter &ccedil;ekilişi</strong>&nbsp;&uuml;zerinden işlemler yapılabilir.&nbsp;Yapılan işlemlerde&nbsp;tweet&rsquo;in linkini&nbsp;&nbsp;girmiş olmanız ve gerekli adımları takip etmeniz &ouml;nemlidir.</span></p>\r\n\r\n<p><strong><span style=\"font-size:20px\">Twitter &Ccedil;ekilişi Yapma Yolları&nbsp;</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">&Ccedil;ekiliş&nbsp;yapmak istediğiniz&nbsp;link hangisiyse bunu giriş&nbsp;yaparak bir&nbsp;paylaşımı&nbsp;retweet edenlere g&ouml;re işlem ger&ccedil;ekleştirilebilir.&nbsp;Twitter alıntı &ccedil;ekilişlerinde&nbsp;tweet&nbsp;linkleri girişi&nbsp;yapılarak işlemler&nbsp;s&uuml;rd&uuml;r&uuml;l&uuml;r.&nbsp;Retweet &ccedil;ekiliş&nbsp;işlemlerinde tweet&rsquo;inlinkini&nbsp;&nbsp;girmeli ve devam etmelisiniz. Bu alanda şu bilgiler istenecektir.<br />\r\n&bull;&nbsp;Kazanan&nbsp;kişi sayısı<br />\r\n&bull;&nbsp;&Ccedil;ekiliş&nbsp;ismi<br />\r\n&bull;&nbsp;&Ccedil;ekiliş&nbsp;tweeti yedek kazanan<br />\r\n&bull;&nbsp;Her&nbsp;kullanıcıyı 1 kere say b&ouml;l&uuml;mlerini doldurmalısınız. &nbsp;<br />\r\nTwitter&rsquo;da yarışma modellerini ger&ccedil;ekleştirmek i&ccedil;in &ouml;ncelikle<br />\r\n&nbsp;nasıl bir &ccedil;ekiliş gerekiyorsa bu konuda&nbsp;duyuruları bulmalısınız. Takip edilen duyurular &uuml;zerinden başvuru şartları ve işlem detaylarını g&ouml;rerek ilerlemelisiniz.&nbsp;<strong>satılık eski tarihli twitter hesapları</strong></span><br />\r\n<span style=\"font-size:20px\">Twitter&rsquo;da yarışma&nbsp;bir a&ccedil;ıdan tweetlerdeki g&uuml;ncellemeler &uuml;zerinden devam ettiği i&ccedil;in &ccedil;ekilişlerdeki ayrıntılar da değişkendir. Yarışmaların&nbsp;yasal&nbsp;kuralları a&ccedil;ısından&nbsp;bir &ccedil;ekiliş yapılırken bu anlamda fayda olacaktır.<br />\r\n<strong>Firmalar&nbsp;A&ccedil;ısından &Ccedil;ekiliş Yapmak</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px\">Bir firma reklam amacıyla kurumsal satışları artırmak i&ccedil;in&nbsp;<strong>twitter &ccedil;ekiliş kampanya</strong>&nbsp;yapabilir. Bu&nbsp;işlemlerde &ouml;nemli olan firmanın geniş bir kitleye ulaşarak beklentileri karşılayan &ccedil;&ouml;z&uuml;mler sunmasıdır. Yani &ccedil;ekilişe katılacak olan kişilerin&nbsp;istedikleri &uuml;r&uuml;nlerle&nbsp;alakalı bir&nbsp;hediye&nbsp;sistemi&nbsp;kurmalısınız. Başarılı&nbsp;bir yarışma i&ccedil;in&nbsp;mutlaka &ouml;nem verilmesi gereken detaylar bulunuyor.<br />\r\n&bull;&nbsp;Takip&ccedil;i&nbsp;sayısını artırmada<br />\r\n&bull;&nbsp;Yorum, beğeni alma<br />\r\n&bull;&nbsp;Genel&nbsp;etkileşimi artırmak gibi faydalar&nbsp;bulunabilir.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Twitter&rsquo;da Yarışma &Ccedil;ekilişi Nasıl Yapılır?</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Twitter&rsquo;da takip&ccedil;iler arasında etkileşimi arttırma, takip&ccedil;i sayısını arttırmak, satışları arttırmak ve beğeni, yorum arttırmak gibi bir&ccedil;ok nedenden dolayı yarışma &ccedil;ekilişleri d&uuml;zenlenmektedir. Hızlı bir şekilde sonu&ccedil; alabileceğiniz bu yarışmalar, twitter&rsquo;da sık sık d&uuml;zenlenmektedir. Kişilerin başarılı bir yarışma sergilemeleri i&ccedil;in dikkat etmeleri gereken hususlar bulunmaktadır.</span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong>Twitter&rsquo;da yarışma &ccedil;ekilişi</strong> yapmadan &ouml;nce t&uuml;m y&ouml;netmeliklere uyulduğundan emin olunması gerekiyor. Kullanıcıların mutlaka bu konuda &ouml;zenli davranması, twitter kurallarını ihlal etmemesi gerekir. Eğer yasalara ve y&ouml;netmeliklere uygun bir &ccedil;ekiliş yapıyorsanız, Tweetterın genel kurallarına g&ouml;z gezdirebilirsiniz. T&uuml;m koşullara uymanız halinde &ccedil;ekiliş yapmanızda herhangi bir sakınca bulunmayacaktır. Twitter&rsquo;da yarışma &ccedil;ekilişi i&ccedil;in duyurunuzu &ouml;nceden yapmanız m&uuml;mk&uuml;nd&uuml;r.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Anlaşılır bir dil kullanarak, kısa ve &ouml;z bir şekilde yarışma koşullarını belirtmeniz son derece &ouml;nemlidir. Yarışma tarihi, yarışma &ouml;d&uuml;l&uuml; ve katılım koşullarını belirtmeniz yararlı olacaktır. Aynı zamanda yarışmanızın kapsamına uygun olacak bir g&ouml;rsel ve hashtag kullanmanız sizin i&ccedil;in olumlu olacaktır. Yarışmanın daha &ccedil;ok kişiye ulaşması i&ccedil;in yapmanız gereken ufak bazı şeyler olabilir. &Ouml;rneğin retweetleyen takip&ccedil;ilerinize hediye verebilirsiniz. Bunu duyurularda a&ccedil;ıklayarak daha &ccedil;ok kişiye ulaşmasını sağlayabilirsiniz. En fazla etkileşimde bulunan takip&ccedil;ilerinizi belirlemeniz i&ccedil;in kullanabileceğiniz programlar vardır. Kazananı a&ccedil;ıklamadan &ouml;nce hesabın sahte olup olmadığını incelemek yararlı olacaktır. &Ccedil;&uuml;nk&uuml; kazananın sahte hesap olması size olan g&uuml;veni sarsacaktır.&nbsp;</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">&Ccedil;ekilişe&nbsp;Girecekler Nelere &Ouml;nem Vermeli?&nbsp;</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Twitter&rsquo;da &ccedil;ekilişe girerek&nbsp;bilgilerini paylaşacak olanların dikkat etmesi gereken en &ouml;nemli konulardan biri&nbsp;t&uuml;m yasa ve y&ouml;netmeliklere uygun&nbsp;bir etkinliktir. Bu şart sayesinde kazanmanız halinde sorun&nbsp;&ccedil;ıkmayacağından&nbsp;emin olursunuz. Tabi&nbsp;sizin de yasa ve y&ouml;netmeliklere uymanız gerekir. &nbsp;Yani sitedeki &ccedil;ekilişte internet bağlantılarında hesaplarda vs. sahte hesap olmaması &ouml;nemlidir.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Yasal a&ccedil;ıdan genelde &ccedil;ekilişlerin neye g&ouml;re belirlendiği konusu Milli Piyango Dairesi&rsquo;yle alakalıdır.&nbsp;Twitter&rsquo;ın genel kuralları a&ccedil;ısından da detaylara &ouml;nem vermek gerekir.<br />\r\nDuyuru yapacak olan firma a&ccedil;ısından dikkat edilmesi gerekenler vardır.&nbsp;Kısa&nbsp;a&ccedil;ık&nbsp;bir dille &ccedil;ekiliş&nbsp;koşullarını takip&ccedil;ilere anlatmak&nbsp;gerekiyor. Kazananları&nbsp;ne bekliyor ve hangi durumlarda nasıl bir s&uuml;re&ccedil; işleyecek bunu net şeffaf bir şekilde anlatmanız&nbsp;&ouml;nemlidir.&nbsp;<strong>Twitter&nbsp;&ccedil;ekiliş sonu&ccedil;ları</strong> a&ccedil;ısından da doğru y&ouml;nlendirme olmalıdır.</span><br />\r\n&nbsp;</p>\r\n', '2020-12-31 18:29:17', 0, 0, '0', 'twitter-cekilisi-nedir-nasil-yapilir', 'Twitter,Çekilisi,nedir,nasil,yapilir', 'Twitter’da çekilişe girerek bilgilerini paylaşacak olanların dikkat etmesi gereken en önemli konulardan biri tüm yasa ve yönetmeliklere uygun bir etkinliktir', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(18, '1494705820_hraehearhear.jpg', 'Twitter Hesabı Gizliye Alma', '<p><span style=\"font-size:20px\">Kullandığınız&nbsp;<strong>Twitter hesabını gizliye almak</strong>&nbsp;istiyorsanız bunun bir&ccedil;ok nedeni olabilir.&nbsp;Hesap akışında sorunla karşılaşanlar i&ccedil;in bu işlem yapılabilir.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Twitter Hesabı Gizliye Alma Neden Yapılır?</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px\">Kullandığınız hesabı başka&nbsp;hesaplardan gelen rahatsız edici mesajlar nedeniyle gizliye almak&nbsp;isteyebilirsiniz. Kullandığınız hesabın şifresi&nbsp;vs. s&uuml;rekli&nbsp;başkaları&nbsp;tarafından ele ge&ccedil;iriliyorsa vs. bu gibi hallerde hesabınızı gizliye&nbsp;alarak kolaylık&nbsp;sağlayabilirsiniz.&nbsp;Bu işlemlerde temel ama&ccedil; aslında g&uuml;venliği&nbsp;artırıcı&nbsp;işlemler sağlamaktır.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Atılan Tweet&rsquo;leri Korumaya Almak</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Belirli sebeplerden dolayı tweetlerinizi koruma altına almak isteyebilirsiniz. Siz koruma altına alma işlemeni yaptıktan sonra, sizi takip eden kişiler dışında hi&ccedil; kimse atmış olduğunuz tweetleri ve sizin ile ilgili bilgileri g&ouml;remeyecektir. Hesabınızı takip&ccedil;iniz olmayan kişilerin g&ouml;rmesini istemiyorsanız, koruma altına alma işlemini yapabilirsiniz. Attığınız twitter i&ccedil;in&nbsp;korumalı işlem yapabilmek i&ccedil;in şu adımları takip etmelisiniz:<br />\r\n&bull;&nbsp;Hesabınıza şifrenizle giriş yapın&nbsp;<br />\r\n&bull;&nbsp;Sağ &uuml;st kısımdan ayarlar b&ouml;l&uuml;m&uuml;ne gelin.<br />\r\n&bull;&nbsp;Gizlilik ve g&uuml;venlik&nbsp;b&ouml;l&uuml;m&uuml;n&uuml; se&ccedil;erek sayfa başında yer alan &ldquo;Twitlerini Korumaya Al&rdquo;&nbsp;b&ouml;l&uuml;m&uuml;n&uuml; işaretleyin.<br />\r\n&bull;&nbsp;En son da kaydederek<strong>&nbsp;hesap&nbsp;koruma&nbsp;twitter</strong>&nbsp;işlemi tamamlayın.<br />\r\n&bull;&nbsp;Yeniden şifre istediğinde şifre ekleyin&nbsp;ve kaydedin.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Android&nbsp;ve&nbsp;İOS&nbsp;Twitter Hesabı Gizleme</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Hesabınızı kullanırken bunu sadece takip&ccedil;ilerinizin g&ouml;rmesini isteyenler i&ccedil;in&nbsp;hesabınızı korumaya alabilirsiniz. Gizliye alırken Android cihazlarda&nbsp;hesaba giriş yapıldığında sol &uuml;st kısma tıklanarak ayarlar gizlilik denir ve &ldquo;Tweetlerimi Korumaya Al&rdquo; denir.&nbsp;Bu işlem&nbsp;İOS telefonlarda da aynı s&uuml;re&ccedil; yapılır.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Bu hesabı korumaya alma işlemi yapılmadığında d&uuml;nyanın her yerinden Twitter&nbsp;girişi yapan kişilere karşı hesabınız g&ouml;r&uuml;nt&uuml;lenebilir d&uuml;zeyde olur.<br />\r\nKısıtlı kişinin g&ouml;receği paylaşımlar zararlı olabilecek sahte hesaplar veya zararlı ger&ccedil;ek hesaplar i&ccedil;in &ouml;nemli bir &ouml;nlem gibi d&uuml;ş&uuml;n&uuml;lebilir<strong>. twitter hesap satışı</strong></span><br />\r\n<span style=\"font-size:20px\">&nbsp;Paylaştığınız videolar, yazılar vs. konusunda herkesin g&ouml;rmesini istemediğiniz durumlarda &ouml;nemli bir işlem s&ouml;z konusu olacaktır.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Twitter Hesabını Gizliden &Ccedil;ıkarmak</strong></span><br />\r\n<span style=\"font-size:20px\">Twitter&rsquo;a kaydolduktan sonra tweetlerinizi koruma altına almak ya da herkese a&ccedil;ık durumuna getirmek tamamen sizin tercihinize kalmış bir durumdur. İstediğiniz zaman tweetlerinizi koruma altına alabilir ya da twitter hesabını gizliden &ccedil;ıkarma işlemini yapabilirsiniz.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Tweetlerinizin korumasını kaldırmak i&ccedil;in belirli talimatları uygulamanız gerekmektedir. Cihazınıza g&ouml;re tweetlerinizin korumasını kaldırmanız m&uuml;mk&uuml;nd&uuml;r. <strong>satılık twitter hesapları</strong></span><br />\r\n<span style=\"font-size:20px\">&Ouml;rneğin; twittera webden giriş yapmışsanız yapmanız gereken tweetlerimi korumaya al se&ccedil;eneğinin yanında yer alan kutunun işaretini kaldırmaktır. Ya da Android cihazlardan ve İOS girişi yapıyorsanız yapmanız gereken twitter uygulamasında tweetlerini korumaya al yazının yanında bulunan kaydırıcıdaki kutunun işaretini kaldırmaktır.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Tweetlerinizi herkese a&ccedil;ık hale getirmek ve hesabınızı gizliden &ccedil;ıkarmak istiyorsanız, bekleyen takip&ccedil;i isteklerinize g&ouml;z gezdirdiğinizden mutlaka emin olmalısınız. Eğer bekleyen takip&ccedil;i istekleri varsa bunlar otomatik olarak kabul edilmeyecektir. Bekleyen durumda bırakılan hesapların sizi tekrar takip etmesi gerekebilir. Twitter&rsquo;da hesabınızın gizliliğini kaldırdıktan sonra, daha &ouml;nce koruma altına alınmış olan tweetleriniz tekrar herkese a&ccedil;ık durumuna gelecektir. Daha &ouml;nce korumalı olan tweetler tekrar g&ouml;r&uuml;nmeye başlar. T&uuml;m tweetlerinizi herkesin g&ouml;rebileceğinden emin olduktan sonra gereken koruma işlemlerini yapmalısınız.&nbsp;</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Twitter Gizlilik Ayarları &Uuml;zerinden Başka Ne Yapılabilir?</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Gizlilik&nbsp;ayarları &uuml;zerinden&nbsp;Twitter platformunda başka değişiklikler de yapabilirsiniz.&nbsp;&Ouml;rneğin&nbsp;birinin sizi fotoğraflarda&nbsp;etiketlemesi konusunda engelleme onay kontrol&uuml; sağlanabilir.&nbsp;Maille&nbsp;ve telefonla iletişime ge&ccedil;ilmesini engelleyebilirsiniz.&nbsp;Ayrıca başkalarından direkt mesaj alma durumunu kontrol edebilirsiniz.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Kendi hesabınızı&nbsp;kullanırken arama sonu&ccedil;larında engellenmiş ve sessize alınmış hesaplarda&nbsp;kontrol imkanı olacaktır.<strong> Twitter hesap gizleme koruma</strong>&nbsp;işlemleri yapılırken&nbsp;amaca uygun şekilde hizmet almış olursunuz.&nbsp;Sitenin&nbsp;kendi i&ccedil;inde koruma&nbsp;mekanizmasını&nbsp;ayarlar b&ouml;l&uuml;m&uuml;nden dilediğiniz şekilde oluşturabilir değiştirebilirsiniz.&nbsp;Hesabınızda belirli aralıklarla şifre değiştirmek de genel g&uuml;venlik a&ccedil;ısından &ouml;nemli olacaktır.</span><br />\r\n&nbsp;</p>\r\n', '2021-01-01 14:20:42', 0, 0, '0', 'Twitter-Hesabi-gizliye-Alma', 'Twitter,Hesabi,gizliye,Alma', '​​​​​​​Kullandığınız Twitter hesabını gizliye almak istiyorsanız bunun birçok nedeni olabilir. Hesap akışında sorunla karşılaşanlar için bu işlem yapılabilir.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL);
INSERT INTO `yazilar` (`yazi_id`, `yazi_foto`, `yazi_title`, `yazi_icerik`, `yazi_tarih`, `yazi_kategori_id`, `yazi_kategori_alt_id`, `yazi_okunma`, `yazi_seolink`, `yazi_keyw`, `yazi_desc`, `created_at`, `updated_at`, `deleted_at`) VALUES
(19, '1353708347_erherherher.jpg', 'Twitter Askıya Alınan Hesap Açma', '<p><span style=\"font-size:20px\">&Ccedil;eşitli zamanlarda belirli sebeplerden dolayı hesaplar askıya alınabilmektedir ve Twitter askıya alınan hesap a&ccedil;ma işlemi o zaman en merak edilen işlemler arasında yer almaktadır.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Twitter daha iyi bir ortam sunmak i&ccedil;in s&uuml;rekli g&uuml;ncellemeler ve hesap kontrolleri yapmaktadır. Bu durum karşısında zaman zaman hesapları askıya almaktadır. Ancak&nbsp;<strong>Twitter hesap askıya alma</strong>&nbsp;işleminin yanlış olduğunu d&uuml;ş&uuml;nebilirsiniz. O zaman da buna itiraz edebilirsiniz. Bu durumda yapılması gereken Twitter kuralları &ccedil;er&ccedil;evesinde form oluşturmak olacaktır. Bu form ile hesabınızın askıya alınması işlemine itirazda bulunabilirsiniz.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Twitter Hesaplar Neden Askıya Alınır?</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">twitter kullanıcılarına g&uuml;venilir bir ortam sunmak amacıyla hesaplar askıya alınabilmektedir. Twitter Kurallarını ihlal eden kullanıcıların hesapları askıya alınmakta ve belirli bir s&uuml;re i&ccedil;in eylemleri sınırlandırılmaktadır. Twitter&rsquo;da hesabın askıya alınmasının bazı nedenleri bulunmaktadır. Bu nedenler şunlardır;<br />\r\nTehlikede olan hesap g&uuml;venliği; Eğer twitter bir hesabın başkaları tarafından ele ge&ccedil;irildiğinden ş&uuml;phelenirse bu durumda potansiyel k&ouml;t&uuml; ama&ccedil;lı etkinliklerin olması nedeniyle hesabı askıya alabilmektedir. Hesap sahibi erişimi tekrar sağlayana kadar hesap askıda kalacaktır.&nbsp;<br />\r\nSpam; twitter&rsquo;da bir&ccedil;ok hesap spam nedeniyle askıya alınmaktadır. Sahte ya da Twitter kullanıcıları i&ccedil;in g&uuml;venliklerini tehlikeye d&uuml;ş&uuml;rebilen hesaplar askıya alınabilmektedir. Bu t&uuml;r hesaplar Twitter Kurallarına aykırı oldukları i&ccedil;in askıya alınmaktadır. Bazı durumlarda bazı ger&ccedil;ek kişilerin hesapları askıya alınabilmektedir. Bu durumda hesap tekrar a&ccedil;ılacaktır.&nbsp;<br />\r\nTaciz i&ccedil;eren davranış ya da twitterler hesapların askıya alınması i&ccedil;in en &ouml;nemli nedenlerden biridir. Twitter Kurallarını ihlal eden taciz i&ccedil;eren davranışlarda bulunulursa ya da tweetler atılırsa bu durumda hesaplar askıya alınabilir. Aynı zamanda başka bir hesap kullanıcısını tehdit etmekte hesabın askıya alınması i&ccedil;in neden oluşturmaktadır. Eğer bir kullanıcı başka bir hesabı taklit ederse hesap ge&ccedil;ici olarak askıya alınabilmektedir. Bazı durumlarda Twitter Kuralları gereği bazı hesaplar kalıcı olarak askıya alınabilmektedir. <strong>Twitter hesaplar askıya alınma</strong> <strong>nedenleri</strong> basit değildir. Spam nedeni ile birlikte taciz i&ccedil;eren davranışlar, tehdit, hesap g&uuml;venliği gibi nedenler dolayı hesap askıya alınabilmektedir.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Askıya Alınan Hesap A&ccedil;ma Nasıl Yapılır?</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px\">&Ouml;ncelikle ger&ccedil;ekten hesabınızın yanlışlıkla askıya alındığından emin olmalısınız. Form doldurma sayfasına girdikten sonra&nbsp;<strong>Twitter askıya alınan hesap a&ccedil;ma</strong>&nbsp;işlemleri başlamaktadır. Bu formda bulunan bilgilerin doldurulması gerekmektedir.<br />\r\n&Ouml;ncelikle bu sorunla nerede karşılaştınız sorusuna, web se&ccedil;eneğini işaretleyerek başlayabilirsiniz. Daha sonra sorun ile ilgili tanım kısmının doldurulması işlemi yapılacak. Burada yapılan işlemin yanlış, olduğunu bildiren ve neden askıya alınmaması gerektiği ile ilgili savunma c&uuml;mlelerinizi yazmalısınız. Sonrasında bilgileriniz kısmında bulunan alanları kendi bilgilerinize g&ouml;re doldurun.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Bu formu yolladıktan sonra Twitter size bir geri d&ouml;n&uuml;ş maili yollayacak. O mailin yanıtlanması gerekmektedir. Ancak bunun i&ccedil;erisine herhangi bir şey yazmanıza gerek yoktur. Geri d&ouml;n&uuml;ş mailini attıktan sonra size 24 ile 48 saat i&ccedil;erisinde durum ile ilgili yeni mail gelecektir.&nbsp;Hesabınız yanlışlık ile askıya alındı ise bu işlem iptal edilecek ve hesabınız yeniden aktif hale gelecektir. Ancak kalıcı olarak iptal edildiği cevabı almanız da muhtemeldir.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Hesap Kalıcı Olarak Askıya Alındı</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong>Twitter hesabı kalıcı askıya alma</strong>&nbsp;işlemi ile sonu&ccedil;lanan mailiniz i&ccedil;in yapılması gereken işlemler TR sunucuları &uuml;zerinden bir daha m&uuml;mk&uuml;n olmayacaktır. Bu durumda diğer &uuml;lkelerin sunucularını kullanabilirsiniz. Bu durumda elbette yapılacak işlem VPN a&ccedil;mak olacaktır.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">VPN a&ccedil;arak yeniden form g&ouml;nderme işlemlerini tekrarlayabilirsiniz. Ancak burada dikkat edilmesi gereken konu hangi dille yazılacağıdır. Burada hangi &uuml;lkeye g&ouml;nderecekseniz o &uuml;lkenin dilinde yazmanız gerekmektedir. B&uuml;y&uuml;k oranda g&ouml;nderdiğiniz mail olumlu sonu&ccedil;lanacak ve hesabınız yeniden aktif hale gelecektir.</span><br />\r\n&nbsp;</p>\r\n', '2021-01-02 14:46:33', 0, 0, '0', 'twitter-askiya-alinan-hesap-acma', 'Twitter,Askıya,Alınan,Hesap,Açma', 'Çeşitli zamanlarda belirli sebeplerden dolayı hesaplar askıya alınabilmektedir ve Twitter askıya alınan hesap açma işlemi o zaman en merak edilen işlemler arasında yer almaktadır.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
(20, '449561399_hreahaerhae.jpg', 'Twitter E Posta Değiştirme', '<p><span style=\"font-size:20px\">Twitter&nbsp;<strong>e posta değiştirme</strong>&nbsp;işlemleri &ccedil;eşitli etkenlere bağlı olarak ihtiya&ccedil; duyulan bir şeydir. Mailiniz g&uuml;ncellendiyse&nbsp;bilgilerinizin sorun&nbsp;&ccedil;ıkarmaması&nbsp;i&ccedil;in bunu&nbsp;yapabilirsiniz.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Twitter&nbsp;E Posta G&uuml;ncelleme Nedir?</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Twitter hesabı kullananlar i&ccedil;in&nbsp;g&uuml;ncel bilgilerinizi nasıl değiştireceğiniz i&ccedil;in bazı adımları takip edebilirsiniz.&nbsp;Hesapla alakalı g&uuml;venlik artırıcı &ccedil;alışmalarda&nbsp;mail adresinizi sorunsuz şekilde yenileyebilirsiniz.&nbsp;G&uuml;ncel hesap g&uuml;venliğinizin de devam edebilmesi i&ccedil;in mail adresinizi yenilendiğinde değiştirmeniz son derece &ouml;nemlidir.&nbsp;Hesap doğrulama&nbsp;gerektiğinde mailiniz&nbsp;bu anlamda destek olacaktır.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Eski&nbsp;Posta İle Alakalı Bildirime Dikkat</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Kullandığınız sitede&nbsp;twitter mail g&uuml;ncelleme i&ccedil;in eski mail adresinize bir g&ouml;nderim gelecektir. Bu g&ouml;nderimde&nbsp;size belirtilen y&ouml;nlendirmeleri takip etmelisiniz.&nbsp;Hesabınızla&nbsp;ilişkili e-posta&nbsp;adreslerinizi doğru&nbsp;bi&ccedil;imde tanımlamak i&ccedil;in aşamaları takip edebilirsiniz.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Twitterda E Posta G&uuml;ncelleme Nasıl Yapılır?</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Talimatlarda&nbsp;<strong>twitterda&nbsp;e posta g&uuml;ncelleme</strong>&nbsp;yapılışı&nbsp;i&ccedil;in şu aşamaları takip edebilirsiniz:<br />\r\n&Ouml;ncelikle profilinize gelin ve ayarlar kısmından gizlilik b&ouml;l&uuml;m&uuml;ne tıklayın.<br />\r\nHesap kısmına&nbsp;gelerek e&nbsp;posta b&ouml;l&uuml;m&uuml;ne girin<br />\r\nMail&nbsp;adresinizi girerek bitti deyin.<br />\r\nBurada &ouml;nemli olan bir detay, bir Twitter hesabıyla alakalı olmasıdır. Yani tek mail tek twitter hesabınızla alakalı olabilir. Bunun nedeni de g&uuml;venlik kontrol amacıyladır.</span></p>\r\n\r\n<p><strong><span style=\"font-size:24px\">Mail Adresini Herkes G&ouml;r&uuml;r M&uuml;?</span></strong></p>\r\n\r\n<p><span style=\"font-size:20px\">Giriş yapılan twitter mail adresi herkesin g&ouml;remeyeceği şekilde ayarlanır.&nbsp;Yani herkesin&nbsp;g&ouml;receği alanda mail bilginiz olmaz.&nbsp;Fakat sizi&nbsp;e posta adresiniz &uuml;zerinden bulmalarına izin ver b&ouml;l&uuml;m&uuml;n&uuml; a&ccedil;ık tuttuğunuzda bu gibi hallerde &ouml;teki kullanıcılar mailinizi g&ouml;rebilir.<strong>satılık eski tarihli twitter hesapları</strong></span><br />\r\n<span style=\"font-size:20px\">&nbsp;Bu durum benzer şekilde telefonla da alakalıdır.&nbsp;Hesabı&nbsp;kullanan&nbsp;kişiler&nbsp;doğrulama gibi detaylara &ouml;nem vermesi &ouml;nemlidir.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>E&nbsp;Posta Doğrulama&nbsp;</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px\">Yeni mail&nbsp;adresinizin e&nbsp;posta doğrulama işlemlerini yaparak aktif hale gelmesini&nbsp;sağlamlasınız.&nbsp;Mail g&uuml;ncellemesi yapılırken doğrulama ile g&uuml;venli şekilde hesap kullanımı devam edecektir.&nbsp;Yapılan hesap i&ccedil;i değişikliklerde mutlaka&nbsp;doğrulama s&uuml;re&ccedil;lerine&nbsp;&ouml;nem vermelisiniz.&nbsp;Aksi halde hesapta aksamalar olabilir.&nbsp;&nbsp;<br />\r\nDoğrulama olmadığında ayrıca&nbsp;tweet arşivinizi isteme&nbsp;gibi işlemlere dair de bir&nbsp;talep olmaz.&nbsp;Bu&nbsp;da ayrıca yararlanabileceğiniz&nbsp;<strong>twitter mail adresi&nbsp;</strong>hizmetidir. Sonu&ccedil; olarak kullandığınız hesaptan mail nedeniyle aksama&nbsp;istenmeyeceği&nbsp;i&ccedil;in&nbsp;talep&nbsp;edilen&nbsp;işlemleri&nbsp;kullanmalısınız.</span></p>\r\n\r\n<p><span style=\"font-size:24px\"><strong>Twitter E-posta Alma Sorunu</strong></span></p>\r\n\r\n<p><span style=\"font-size:20px\">&nbsp;Sağlamış olduğunuz e posta adresine g&ouml;nderim yapma sorunu yaşıyorsanız twitter tarafından sarı bir uyaran alırsınız. Bu durumda size bildirimler de gelmeyecektir.&nbsp;<br />\r\nSizin se&ccedil;miş olduğunuz e-posta bildirimlerine g&ouml;re twitter size mesaj, takip&ccedil;i ve bir&ccedil;ok bildirim g&ouml;ndermektedir. E-postaların durdurulması durumunda size &ouml;nemli e-postalarda gelmeyecektir. &Ouml;rneğin şifre sıfırlama gibi &ouml;nemli e-postalar size ulaşmayabilir.<br />\r\n&Ouml;ncelikle yapmanız gereken e-posta adresinizin doğruluğunu kontrol etmektir. Hesabınızda yer alan adresinizi doğru bir şekilde yazdığınızı mutlaka teyit edin. Eğer burada bir hata varsa hesap ayarlarınızdan e-posta adresinizi d&uuml;zenlemelisiniz.&nbsp;<br />\r\nSize g&ouml;nderilen sarı uyaranın i&ccedil;inde yer alan mesaj g&ouml;ndermeyi tekrar dene se&ccedil;eneğine tıklamalısınız. Bu durumda e-postalarınızın g&ouml;nderilmesi tekrar denenecektir. Eğer aksaklık devam ederse size sarı uyaranlar gelmeye devam edecektir.<br />\r\nHala sorun yaşamaya devam ediyorsanız, yapmanız gereken mevcut e-postanıza giriş yapmak ve sizlere tiwitterdan gelen mailleri kontrol etmektir. Spam, istenmeyen e-posta ya da toplu posta gibi t&uuml;m klas&ouml;rlere bakarak size mail gelmediğinden emin olmalısınız.<br />\r\nEğer t&uuml;m bunlara rağmen sorununuz hala devam ediyorsa o halde yapmanız gereken hesap ayarlarınıza giderek e-posta adresinizi yeni bir adresle değiştirmektir. Yeni a&ccedil;mış olduğunuz ya da elinizde var olan yeni bir e-posta ile twitter hesabınızı eşleştirebilirsiniz. Bununla birlikte twitterdan gelen e-postalardan birinde yanlışlıkla &ldquo;Bu benim hesabım değil&rdquo; se&ccedil;eneğine tıklamışsanız size g&ouml;nderilen e-posta g&ouml;nderimi durdurulabilir. Bundan dolayı mutlaka twitter <strong>e-posta alma sorunu</strong>nu &ccedil;&ouml;zmelisiniz.&nbsp;</span><br />\r\n&nbsp;</p>\r\n', '2021-01-03 14:56:15', 0, 0, '0', 'twitter-e-posta-degistirme', 'twitter,e,posta,değiştirme', 'Twitter e posta değiştirme işlemleri çeşitli etkenlere bağlı olarak ihtiyaç duyulan bir şeydir. Mailiniz güncellendiyse bilgilerinizin sorun çıkarmaması için bunu yapabilirsiniz.', '2021-11-23 17:08:59', '2021-11-23 17:08:59', NULL),
